#define OUTER_BOX 0.
#define BOX 1.
#define MAP 2.
#define DOTS 3.
float _seed;
float rand()
{
    _seed++;
    return hash11(_seed);
}
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    float th = 0.007;
    float box = _cucube(p, vec3(1.), vec3(th));
    acc = _min(acc, vec2(box, BOX));

    float outerbox = _cucube(p, vec3(1.2), vec3(th));
    float outerboxrad = .8;
    outerbox = max(outerbox, -(abs(p.x)-outerboxrad));
    outerbox = max(outerbox, -(abs(p.y)-outerboxrad));
    outerbox = max(outerbox, -(abs(p.z)-outerboxrad));
    acc = _min(acc, vec2(outerbox, OUTER_BOX));

    vec3 pbox2 = p;
    float outerbox2 = _cucube(pbox2, vec3(1.8), vec3(th*2.));
    vec3 rep2 = vec3(.2);
    pbox2 = mod(pbox2+rep2*.5,rep2)-rep2*.5;
    outerbox2 = max(outerbox2, _cube(pbox2, vec3(.07)));
    acc = _min(acc, vec2(outerbox2, DOTS));

    float map = p.y-texture(iChannel2, p.xz*.01).x*2.;
    float borderboxmap = max(abs(map)-th, abs(_cube(p, vec3(1.)))-th*2.);
    acc = _min(acc, vec2(borderboxmap, BOX));

    vec3 pcube = p;
    vec2 repcube = vec2(.2);
    pcube.xz = mod(pcube.xz+.5*repcube, repcube)-repcube*.5;
    map = max(abs(map-.2)-th, -_cube(pcube, vec2(.1, 5.).xyx));
    map = max(map, _cube(p, vec3(1.)));
    acc = _min(acc, vec2(map, MAP));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 10.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x*.5;
    }
    return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.110,0.035,0.114);

    float d = 7.;
    float t = iTime *.25;
    vec3 ro = vec3(sin(t)*d,-4.,cos(t)*d);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        if (res.z == OUTER_BOX)
        {
            col = vec3(0.322,0.686,0.271);
        }
        if (res.z == BOX)
        {
            col = mix(vec3(0.663,0.157,0.204), vec3(0.741,0.435,0.314), 1.-sat(abs(uv.y*4.)));
        }
        if (res.z == MAP)
        {
            col = mix(vec3(0.714,0.361,1.000), vec3(0.169,0.059,1.000), 1.-sat(length(p*1.5)));
        }
        if (res.z == DOTS)
        {
            col = vec3(0.220,0.271,1.000);
        }
    }



    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    //vec2 off = .75*(vec2(rand(), rand())-.5)*2.*1./iResolution.x;
    vec3 col = rdr(uv);

    vec2 off = vec2(1., -1.)/(iResolution.x*1.5);

    if (true)//diff > 0.3) // Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    col *= 1.9/(col+1.);
    //col = pow(col, vec3(.4545));
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .7);
    fragColor = vec4(col,1.0);
}

