// Material Maker Experiment 001
// By PauloFalcao
//
// Made in materialmaker just using 7 nodes! :)
//
// MaterialMaker is a nodebased shader maker to make procedural textures
// With custom nodes GLSL nodes created directly in the tool,
// it's possible to make complex stuff like raymarching :)
//
// It's also possible to export the generated code to Shadertoy!
//
// I made a library with Ray Marching nodes
// 1st version 0.01 have 44 new nodes
// Some nodes are based in code from other authors from shadertoy
// I always refer the shader author and the shadertoy original code
// The idea is to reuse the code to quicky create something without coding experience
// Or just focus on the code of a single node
//
// You need Material Maker - https://rodzilla.itch.io/material-maker
// And my library - https://github.com/paulofalcao/MaterialMakerRayMarching
// This Material Maker graph is here
// https://raw.githubusercontent.com/paulofalcao/MaterialMakerRayMarching/main/Examples/pxf001.mms
// Just copy paste the code into Material Maker and the graph will appear! :)
// Or save the file and use "Edit"->"Load Selection"
// 


vec3 v4v4_rotate(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

const float PI=3.14159265359;

vec2 equirectangularMap(vec3 dir) {
	vec2 longlat = vec2(atan(dir.y,dir.x),acos(dir.z));
 	return longlat/vec2(2.0*PI,PI);
}


//Simple HDRI START

//Hash without Sine Dave_Hoskins
//https://www.shadertoy.com/view/4djSRW 
float Simple360HDR_hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float Simple360HDR_noise(vec2 v){
  vec2 v1=floor(v);
  vec2 v2=smoothstep(0.0,1.0,fract(v));
  float n00=Simple360HDR_hash12(v1);
  float n01=Simple360HDR_hash12(v1+vec2(0,1));
  float n10=Simple360HDR_hash12(v1+vec2(1,0));
  float n11=Simple360HDR_hash12(v1+vec2(1,1));
  return mix(mix(n00,n01,v2.y),mix(n10,n11,v2.y),v2.x);
}

float Simple360HDR_noiseOct(vec2 p){
  return
    Simple360HDR_noise(p)*0.5+
    Simple360HDR_noise(p*2.0+13.0)*0.25+
    Simple360HDR_noise(p*4.0+23.0)*0.15+
    Simple360HDR_noise(p*8.0+33.0)*0.10+
    Simple360HDR_noise(p*16.0+43.0)*0.05;
}

vec3 Simple360HDR_skyColor(vec3 p){
	vec3 s1=vec3(0.2,0.5,1.0);
	vec3 s2=vec3(0.1,0.2,0.4)*1.5;
    vec3 v=(Simple360HDR_noiseOct(p.xz*0.1)-0.5)*vec3(1.0);
	float d=length(p);
    return mix(s2+v,s1+v*(12.0/max(d,20.0)),clamp(d*0.1,0.0,1.0));
}

vec3 Simple360HDR_floorColor(vec3 p){
    vec3 v=(Simple360HDR_noiseOct(p.xz*0.1)*0.5+0.25)*vec3(0.7,0.5,0.4);
    return v;
}

vec3 Simple360HDR_renderHDR360(vec3 rd, vec3 sun){
    vec3 col;
	vec3 p;
	vec3 c;
	if (rd.y>0.0) {
        p=rd*(5.0/rd.y);
        c=Simple360HDR_skyColor(p);
    } else {
        p=rd*(-10.0/rd.y);
        c=Simple360HDR_floorColor(p);
		c=mix(c,vec3(0.5,0.7,1.0),clamp(1.0-sqrt(-rd.y)*3.0,0.0,1.0));
	}
	vec3 skycolor=vec3(0.1,0.45,0.68);
	float d=length(p);

	float ds=clamp(dot(sun,rd),0.0,1.0);
	vec3 sunc=(ds>0.9997?vec3(2.0):vec3(0.0))+pow(ds,512.0)*4.0+pow(ds,128.0)*vec3(0.5)+pow(ds,4.0)*vec3(0.5);
    if (rd.y>0.0){
		c+=vec3(0.3)*pow(1.0-abs(rd.y),3.0)*0.7;
	} 
    return c+sunc;
}

vec3 Simple360HDR_make360hdri(vec2 p, vec3 sun){
    float xPI=3.14159265359;
    vec2 thetaphi = ((p * 2.0) - vec2(1.0)) * vec2(xPI,xPI/2.0); 
    vec3 rayDirection = vec3(cos(thetaphi.y) * cos(thetaphi.x), sin(thetaphi.y), cos(thetaphi.y) * sin(thetaphi.x));
    return Simple360HDR_renderHDR360(rayDirection,sun);
}
//Simple HDRI END

const float p_o1356810_CamY = 1.450000000;
const float p_o1356810_LookAtX = 0.000000000;
const float p_o1356810_LookAtY = 0.000000000;
const float p_o1356810_LookAtZ = 0.000000000;
const float p_o1356810_CamD = 1.500000000;
const float p_o1356810_CamZoom = 0.695000000;
const float p_o1356810_Reflection = 0.179000000;
const float p_o1356810_Specular = 1.000000000;
const float p_o1356810_Pow = 275.430000000;
const float p_o1356810_SunX = 0.439999998;
const float p_o1356810_SunY = 1.480000019;
const float p_o1356810_SunZ = -0.976000011;
const float p_o1356810_AmbLight = 0.250000000;
const float p_o1356810_AmbOcclusion = 0.391000000;
const float p_o1356810_Shadow = 0.921000000;
const float p_o1356813_r = 0.069900000;

float o1356807_input_obj3d(vec3 p) {
    float o1356813_0_1_sdf3d = length((p))-p_o1356813_r;
    return o1356813_0_1_sdf3d;
}

const float p_o1356804_smooth = 0.000990000;
const float p_o1356801_x = 0.086000000;
const float p_o1356801_y = 0.060000000;
const float p_o1356801_z = 0.096000000;

vec4 o1356807_input_trans3d(vec4 p) {
    vec4 o1356798_0_1_v4v4 = (vec4(v4v4_rotate((vec4((vec4(vec3(sqrt((p).xyz*(p).xyz+p_o1356804_smooth)),(p).w)).xyz-vec3(p_o1356801_x, p_o1356801_y, p_o1356801_z),(vec4(vec3(sqrt((p).xyz*(p).xyz+p_o1356804_smooth)),(p).w)).w)).xyz, -vec3((sin(iTime*4.0)*16.0), (sin(iTime*2.2)*16.0), (sin(iTime*1.3)*16.0))*0.01745329251), (vec4((vec4(vec3(sqrt((p).xyz*(p).xyz+p_o1356804_smooth)),(p).w)).xyz-vec3(p_o1356801_x, p_o1356801_y, p_o1356801_z),(vec4(vec3(sqrt((p).xyz*(p).xyz+p_o1356804_smooth)),(p).w)).w)).w));
    vec4 o1356795_0_1_v4v4 = o1356798_0_1_v4v4;
    vec4 o1356801_0_1_v4v4 = o1356795_0_1_v4v4;
    vec4 o1356804_0_1_v4v4 = o1356801_0_1_v4v4;

    return o1356804_0_1_v4v4;
}

float o1356807_input_custombool(vec2 uv) {
    return min((uv).x,(uv).y);
}

float for_fold_o1356807(vec4 p) {
  for(int i=0;i<6;i++){
    p=o1356807_input_trans3d(p);
  }
  return o1356807_input_obj3d(p.xyz);
}

float for_min_o1356807(vec4 p) {
  float m=999999.0;
  for(int i=0;i<6;i++){
    p=o1356807_input_trans3d(p);
	float d=o1356807_input_obj3d(p.xyz);
	m=min(m,d);
  }
  return m;
}

float for_custom_o1356807(vec4 p) {
  float m=999999.0;
  for(int i=0;i<6;i++){
    p=o1356807_input_trans3d(p);
	float d=o1356807_input_obj3d(p.xyz);
	m=o1356807_input_custombool(vec2(m,d));
  }
  return m;
}

float o1356810_input_sdf_a(vec3 p) {
    float o1356807_0_1_sdf3d = for_fold_o1356807(vec4((p),0.0));
    return o1356807_0_1_sdf3d;
}

vec3 o1356810_input_tex3d_a(vec4 p) {
    return vec3(1.0,0.1,0.1);
}

float o1356810_input_sdf_b(vec3 p) {
    return max((p).y+1.0,length(vec3((p).x,(p).y+1.0,(p).z))-10.0);
}

vec3 o1356810_input_tex3d_b(vec4 p) {
    return vec3(mod(floor((p).x*2.0)+floor((p).z*2.0),2.0))*0.9+0.1;
}

vec3 o1356810_input_hdri(vec2 uv) {
    return Simple360HDR_make360hdri(vec2((uv).x,-(uv).y+1.0),normalize(vec3(-p_o1356810_SunX,p_o1356810_SunY,-p_o1356810_SunZ)));
}

vec2 input_o1356810(vec3 p) {
    float sdfa=o1356810_input_sdf_a(p);
	float sdfb=o1356810_input_sdf_b(p);
	if (sdfa<sdfb) {
	  return vec2(sdfa,0.0);
	} else {
	  return vec2(sdfb,1.0);
	}
}

//tetrahedron normal by PauloFalcao
//https://www.shadertoy.com/view/XstGDS
vec3 normal_o1356810(vec3 p){  
  const vec3 e=vec3(0.001,-0.001,0.0);
  float v1=input_o1356810(p+e.xyy).x;
  float v2=input_o1356810(p+e.yyx).x;
  float v3=input_o1356810(p+e.yxy).x;
  float v4=input_o1356810(p+e.xxx).x;
  return normalize(vec3(v4+v1-v3-v2,v3+v4-v1-v2,v2+v4-v3-v1));
}

void march_o1356810(inout float d,inout vec3 p,inout vec2 dS, vec3 ro, vec3 rd){
    for (int i=0; i < 500; i++) {
    	p = ro + rd*d;
        dS = input_o1356810(p);
        d += dS.x;
        if (d > 50.0 || abs(dS.x) < 0.0001) break;
    }
}

//from https://www.shadertoy.com/view/lsKcDD
float calcAO_o1356810( in vec3 pos, in vec3 nor ){
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ){
        float h = 0.001 + 0.25*float(i)/4.0;
        float d = input_o1356810( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.98;
    }
    return clamp( 1.0 - 1.6*occ, 0.0, 1.0 );    
}

//from https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow_o1356810( in vec3 ro, in vec3 rd, in float mint, in float tmax){
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration
    for( int i=0; i<32; i++ ){
		float h = input_o1356810( ro + rd*t ).x;
        res = min( res, 10.0*h/t );
        t += h;
        if( res<0.0001 || t>tmax ) break;  
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 raymarch_o1356810(vec2 uv) {
    uv-=0.5;
	vec3 cam=vec3((sin(iTime*0.1)*4.0),p_o1356810_CamY,(cos(iTime*0.13)*4.0))*p_o1356810_CamZoom;
	vec3 lookat=vec3(p_o1356810_LookAtX,p_o1356810_LookAtY,p_o1356810_LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*p_o1356810_CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	vec2 dS=vec2(0);
	march_o1356810(d,p,dS,ro,rd);

    vec3 color=vec3(0.0);
	vec3 objColor=(dS.y<0.5)?o1356810_input_tex3d_a(vec4(p,1.0)):o1356810_input_tex3d_b(vec4(p,1.0));
	vec3 light=normalize(vec3(p_o1356810_SunX,p_o1356810_SunY,p_o1356810_SunZ));
	if (d<50.0) {
	    vec3 n=normal_o1356810(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_o1356810(p,n),p_o1356810_AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_o1356810(p,light,0.05,5.0),p_o1356810_Shadow);
		color=min(vec3(max(shadow,p_o1356810_AmbLight)),max(l,p_o1356810_AmbLight))*max(cAO,p_o1356810_AmbLight)*objColor+pow(r,p_o1356810_Pow)*p_o1356810_Specular;
		//reflection
		d=0.01;
		march_o1356810(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		if (d<50.0) {
			objColorRef=(dS.y<0.5)?o1356810_input_tex3d_a(vec4(p,1.0)):o1356810_input_tex3d_b(vec4(p,1.0));
			n=normal_o1356810(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			objColorRef=max(l,p_o1356810_AmbLight)*objColorRef;
		} else {
			objColorRef=o1356810_input_hdri(equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,p_o1356810_Reflection);
	} else {
		color=o1356810_input_hdri(equirectangularMap(rd.xzy)).xyz;
	}
	return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float minSize = min(iResolution.x, iResolution.y);
    vec2 UV = vec2(0.0, 1.0) + vec2(1.0, -1.0) * (fragCoord-0.5*(iResolution.xy-vec2(minSize)))/minSize;
    vec3 o1356810_0_1_rgb = raymarch_o1356810((UV));
    fragColor = vec4(o1356810_0_1_rgb, 1.0);
}
