// updated with the help of @FabriceNeyret2 - thanky!
// check out the shrinked version in the comments!!

// random function
#define rand(n) fract(cos(n*89.42)*343.42)

//draw a even nicer ring
#define nice_ring(U,P,r,w)   w / abs(length(P - U) - r)

// generic rotation by angle
#define rot(a) mat2( cos(a), -sin(a), sin(a), cos(a) )


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


   vec2 uv = ( 2.* fragCoord - iResolution.xy ) / iResolution.y;
    // instead of this 
    //vec2 uv = 2.*( fragCoord.xy / iResolution.xy ) - 1. ;
	//uv.x *= iResolution.x/iResolution.y;

	float t = iTime*.3 , s = 0., r = .55, w = .2,  i = 0. ;

	// rotate all
	uv *= rot(t*.14);

	// slight wiggle all / on both axis
	uv += sin(t*.15)*.3;

	for(;i<27.;i++){

		vec2 posi = vec2(sin(t + i*.23)*.5 , sin(i*.13 + t)*.80 );

		w += sin(i*.3+t*.81)*.02;
		s += nice_ring( uv, posi, r,w*.01 );
		r -=.01;
		w +=.01;

	}

	s = smoothstep(1.,.3,s)*.8-.1;

	// lowkey dithering - very light for nice fullscreen grain
	s -= rand(uv.x)*.04; 

	// tweaky vignette whatever  
	 float vig = uv.x*uv.y * 15.0; 
	  s+=vig*.006-.1;


	fragColor = vec4( vec3(s), 1.0 );


}
