void mainImage( out vec4 O,  vec2 p )
{   
    vec2  R = iResolution.xy;
    float l = length( p -= .5 * R ) / R.y,
          a = atan(p.y,p.x);

	O =     .3 * l 
        + ( .01 + .006 / ( 1.  + sin( 6.*l - 3.*iTime ) *.8 ) ) 
          / abs(  sin( 15.*a) 
                * sin( l*l*l*27. -1.5*iTime - a*.5 - l ) 
                * sin( l*2.7 ) 
               ) - O;
}

