void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 0.8 * iTime;
    float div = 1.;
    // Normalized pixel coordinates (from 0 to 1)
    fragCoord = mod(fragCoord, div * iResolution.xy);
    vec2 uv = (fragCoord - div *  0.5 * iResolution.xy)/iResolution.y;
    //uv = mod(uv, 0.5);
    float theta = atan(uv.y, uv.x);
    float d = length(uv);
    uv /= 0.03 + 0.01 * d;
    uv += 2. * cos(uv.x + 4. * 3.14159 * cos(0.5* t)) + 2. * sin(uv.y + 2. * 3.14159 * sin(0.3 *t));
    theta -= atan(uv.y,uv.x);
    d = length(uv);
    d = mod(d,1.4);
    float s = 0.5 * (1. + cos(4. * theta + t));
   // s = s * s;
    float val = step(9./d,2.+10. * s);//,
    //0. - 7. * (1.-s) + s * 7. * cos(5. * theta + t));
    val *= 1.-0.08 * length(uv);
    // Output to screen
    fragColor = vec4(val);
}
