float displacement(vec3 p)
{
  float val=0.92;  
  //return sin(val*p.x)*sin(val*p.y)*sin(val*p.z);
  return sin(val*p.x)*sin(val*p.y)*sin(val*p.z);
}

float opDisplace( float dist, in vec3 p )
{   float d1 =dist; //primitive(p);
    float d2 = displacement(p);
    return d1+d2;
}

float arbol(vec3 p)
