#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrFlatCapsDf (vec3 p, float r, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float PrTorusBxDf (vec3 p, vec3 b, float ri);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float Minv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = (id); }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = (id);  qHit = q;}
#define DMINQTR(id) if (d < dMin) { dMin = d;  idTrObj = (id);  qTrHit = q;}

vec3 qHit, qTrHit, pgSize, ltDir, ltDirS, wlBase, carPos;
vec2 wlRot;
float tCur, dstFar, szFac, wlRad, strRot, carRot, spd, dInt, dWarch, pSign;
int idObj, idTrObj;
bool isSk;
const int idBod = 1, idTyr = 2, idHub = 3, idDash = 4, idWarch = 5, idBase = 6, idExR = 7,
   idExF = 8, idWscrn = 9, idSeat = 10, idStWhl = 11, idPlat = 12, idMLit = 13, idCLit = 14,
   idSLit = 15, idRLit = 16, idAx = 17, idShin = 18, idLtSil = 19, idDkSil = 20,
   idFrm = 22;
const float pi = 3.1415927;

float WheelDf (vec3 q, float dMin)
{
  float d;
  d = max (PrRoundCylDf (q, wlRad - 0.05, 0.05, 0.15), 0.5 - length (q.xy));
  DMINQ (idTyr);
  d = min (PrCylDf (q, 0.5, 0.13), PrCylDf (q, 0.2, 0.17));
  DMINQ (idHub);
  return dMin;
}

float CarExDf (vec3 p, float dMin)
{
  vec3 q, qq;
  vec2 s;
  float d, r, h, dw, t;
  q = p;
  d = PrCylDf (vec3 (q.x, abs (q.y) - 0.07, q.z - 2.7).yzx, 0.04, 1.55);
  DMIN (idAx);
  d = PrCylDf (vec3 (abs (abs (q.x) - 1.11) - 0.19, q.y, q.z - 2.7).xzy, 0.06, 0.11);
  DMIN (idAx);
  d = PrCylDf (vec3 (q.xy, q.z + 2.7).yzx, 0.1, 1.55);
  DMIN (idAx);
  q = p;
  q.xz = abs (q.xz) - wlBase.xz;
  qq = q;
  qq.xz = (p.z > 0.) ? Rot2D (qq.xz, - strRot * sign (p.x)) : qq.xz;
  qq.yz = Rot2D (qq.yz, - ((p.x < 0.) ? wlRot.x : wlRot.y) * sign (p.z));
  dMin = WheelDf (qq.zyx, dMin);
  q.x -= -0.1;
  r = 0.8;
  r *= (1. + 0.5 * smoothstep (0., 0.3 * r, q.z) * smoothstep (r, -0.2 * r, q.y));
  h = (p.z > 0.) ? 0.1 * (q.z + 0.4) : 0.;
  dWarch = PrFlatCapsDf (vec3 (q.x + h, q.yz).yzx, r, 0.35 * (1. - 0.2 * q.z) + 1.1 * h);
  d = max (max (abs (dWarch) - 0.02, -0.21 - q.y), min (0.9 - length (q.yz), q.x));
  if (p.z > 0.) d = SmoothMax (d, - max (0.52 - length (vec2 (q.x + 0.15, 0.5 - q.z)),
     0.4 - q.z), 0.02);
  d *= 0.7;
  DMINQ (idWarch);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (0.7, -3.5), 0.68 * pi);
  dw = PrCylDf (q, wlRad + 0.01, 0.15);
  dMin = WheelDf (q, dMin);
  q.z -= 0.18;
  d = min (PrSphDf (q, 0.08), PrCylDf ((q - vec3 (0., 0.12, 0.04)).xzy, 0.03, 0.12));
  DMIN (idDkSil);
  q = p;
  qq = q;
  qq.z -= -0.5;
  dInt = PrRoundBoxDf (vec3 (qq.x, qq.y - 0.8, qq.z), vec3 (vec2 (0.85, 1.2) -
     vec2 (0.03, 0.05) * qq.zx * qq.zx, 0.8).xzy, 0.1);
  qq.z -= -0.1;
  h = 0.8 * (1. - smoothstep (0.4, 1.4, - qq.z / 3.5));
  qq.y -= -0.2 + h;
  r = 0.3 * (1. + 0.3 * (qq.z - 2.) * (qq.z - 2.) * step (2., qq.z));
  d = PrRoundBoxDf (qq, vec3 (1.1 - ((qq.z > 0.) ? 0.035 : 0.005) * qq.z * qq.z - r,
     h - r, 3.5), r);
  d = SmoothMax (max (d, qq.z - 3.8 + 0.4 * qq.x * qq.x), - dInt, 0.05);
  qq.y -= -0.8;
  d = min (d, max (max (length (qq.xy) - 0.15, - qq.y), dInt));
  d = 0.7 * SmoothMax (max (d, - dWarch), - dw, 0.05);
  DMINQ (idBod);
  q = p;
  q.yz -= vec2 (-0.18, -0.2);
  d = PrRoundBoxDf (q, vec3 (1.4, 0.01, 3.71), 0.02);
  d = max (SmoothMin (d, max (PrCylDf (vec3 (q.x, q.y + 0.2, q.z - 2.9).yzx, 0.5, 1.4), 0.03 -
     q.y), 0.03), - dWarch);
  DMINQ (idBase);
  q = p;
  q -= vec3 (-0.8, 0.4, 1.4);
  s = sin (-0.1 * pi + vec2 (0.5 * pi, 0.));
  q.yz = Rot2Cs (q.yz, s);
  s = Rot2Cs (vec2 (0., 0.15), s);
  d = PrTorusBxDf (vec3 (q.x, q.y - s.x * sign (q.z), abs (q.z) - s.y), vec3 (0.1, 0.25,
     0.2), 0.08);
  DMINQ (idExF);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (1.6, 0.8), 0.15 * pi);
  q.z -= -0.05 * abs (q.x);
  d = PrRoundBoxDf (q, vec3 (0.95, 0.4, 0.005), 0.015);
  d = 0.9 * max (d, - PrRoundBox2Df (vec2 (abs (q.x), q.y) - vec2 (0.45, 0.1),
     vec2 (0.4, 0.24), 0.02));
  DMIN (idWscrn);
  q.x = abs (q.x);
  d = PrCylDf ((q - vec3 (0.3, -0.18, 0.03)).yzx, 0.02, 0.2);
  DMIN (idLtSil);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.7, -0.25, -3.);
  d = max (abs (PrCapsDf (q, 0.06, 1.4)) - 0.02, -1.4 - q.z);
  DMINQ (idExR);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (1., 1.05, 0.3);
  d = PrTorusBxDf (q.xzy, vec3 (0.08, 0.1, 0.03), 0.02);
  DMIN (idShin);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (1.05, 1.4, 0.85);
  d = max (PrSphDf (q, 0.1), q.z - 0.05);
  DMINQ (idSLit);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (1.3, 1.2, 2.9);
  d = max (max (PrSphDf (q, 0.1), q.z - 0.05), - dWarch);
  DMINQ (idSLit);
  q = p;
  q.yz -= vec2 (0.6, 3.4);
  d = max (PrCylDf (q.yzx, 0.02, 1.2), - dWarch);
  DMIN (idShin);
  q.y -= 0.14;
  d = max (PrSphDf (q, 0.14), q.z - 0.04);
  DMINQ (idCLit);
  q.x = abs (q.x);
  q.xy -= vec2 (0.55, 0.11);
  d = SmoothMax (abs (PrSphDf (q, 0.24)) - 0.01, q.z - 0.08, 0.01);
  DMINQ (idMLit);
  q.xy -= vec2 (-0.3, -0.3);
  t = q.z + 0.2;
  d = PrCylAnDf (q, 0.03 * (1. + 10. * t * t), 0.01, 0.2);
  q.z -= -0.18;
  d = min (d, PrCylDf (q, 0.08, 0.015));
  DMIN (idShin);
  q = p;
  q.x = abs (q.x);
  q.xz -= vec2 (0.5, -4.37);
  d = PrCylDf (q, 0.07, 0.03);
  DMINQ (idRLit);
  q = p;
  q.yz -= vec2 (1.5, 2.95);
  d = PrSphDf (q, 0.11);
  if (d < 0.05) {
    d = PrTorusDf (q, 0.016, 0.1);
    q.xy = Rot2D (q.xy, - pi / 6.);
    q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
    q.x += 0.05;
    d = min (d, PrCylDf (q.yzx, 0.016, 0.05));
    DMIN (idShin);
  } else dMin = min (dMin, d);
  q = p;
  pSign = sign (q.z);
  q.z = abs (q.z + 0.41);
  q.yz -= vec2 (((p.z > 0.) ? -0.25 : -0.1), 3.96);
  d = PrRoundBoxDf (q, vec3 (0.3, 0.13, 0.01), 0.01);
  DMINQ (idPlat);
  return dMin;
}

float CarInDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 s;
  float d, t;
  q = p;
  q.yz -= vec2 (0.3, -0.1);
  d = PrRoundBoxDf (q, vec3 (0.85, 0.07, 0.3), 0.05);
  q.yz = Rot2D (q.yz - vec2 (0.5, -0.4), 0.57 * pi);
  d = min (d, PrRoundBoxDf (q, vec3 (0.85, 0.07, 0.5), 0.05));
  d = SmoothMax (d, 0.03 - abs (q.x + 0.05), 0.05);
  DMINQ (idSeat);
  q = p;
  q.yz -= vec2 (0.3, -1.15);
  d = PrRoundBoxDf (q, vec3 (0.8, 0.07, 0.3), 0.05);
  q.yz = Rot2D (q.yz - vec2 (0.5, -0.4), 0.57 * pi);
  d = min (d, PrRoundBoxDf (q, vec3 (0.8, 0.07, 0.5), 0.05));
  DMINQ (idSeat);
  q = p;
  t = 0.05 * q.x * q.x;
  q.yz -= vec2 (1.1 - t, 0.8);
  d = 0.7 * max (max (PrRoundBox2Df (q.yz, vec2 (0.25 * (1. - t), 0.12), 0.02),
     - PrCylDf (vec3 (abs (q.x - 0.42) - 0.25, q.y, q.z + 0.12), 0.17, 0.03)), dInt);
  DMINQ (idDash);
  q = p;
  q -= vec3 (0.42, 1.1, 0.35);
  q.yz = Rot2D (q.yz, -0.15 * pi);
  d = PrTorusDf (q, 0.02, 0.33);
  DMIN (idLtSil);
  d = PrCylDf (q, 0.1, 0.02);
  DMINQ (idStWhl);
  s = q.xy;
  q.xy = Rot2D (q.xy, 0.25 * pi - 8. * strRot);
  q.xy = (abs (q.x) < abs (q.y)) ? q.xy : q.yx;
  q.y = abs (q.y) - 0.17;
  d = PrCylDf (q.xzy, 0.015, 0.17);
  q.xy = s;
  q.z -= 0.5;
  d = min (d, PrCylDf (q, 0.03, 0.5));
  DMIN (idDkSil);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (0.2, 0.45), 0.1 * pi * sign (spd));
  d = PrCylDf (q.xzy, 0.025, 0.4);
  DMIN (idDkSil);
  q.y -= 0.4;
  d = PrSphDf (q, 0.06);
  DMIN (idLtSil);
  q = p;
  q -= vec3 (0.42, 0., 0.65);
  q.x = (q.x > 0.) ? abs (q.x - 0.25) - 0.08 : q.x + 0.2;
  q.yz = Rot2D (q.yz, 0.1 * pi);
  d = PrRoundBoxDf (q, vec3 (0.03, 0.02, 0.1), 0.02);
  DMIN (idDkSil);
  return dMin;
}

float ObjDf (vec3 p)
{
  float dMin, d;
  p /= szFac;
  p.xz = Rot2D (p.xz - carPos.xz, carRot);
  p.yz -= vec2 (carPos.y + wlRad + 0.1, wlBase.z);
  d = PrRoundBoxDf (p - vec3 (0., 0.6, 0.), vec3 (2., 1.4, 4.5), 0.01);
  if (d < 0.1) {
    dMin = CarExDf (p, dstFar);
    if (dInt < 0.1) dMin = CarInDf (p, dMin);
    else dMin = min (dMin, dInt);
  } else dMin = d;
  return szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  p /= szFac;
  p.xz = Rot2D (p.xz - carPos.xz, carRot);
  p.yz -= vec2 (carPos.y + wlRad + 0.1, wlBase.z);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (1.6, 0.8), 0.15 * pi);
  q.z -= -0.05 * abs (q.x);
  d = 0.9 * PrBoxDf (q, vec3 (0.95, 0.4, 0.01));
  DMINQTR (idWscrn);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.55, 0.83, 3.42);
  d = PrCylDf (q, 0.24, 0.02);
  DMINQTR (idMLit);
  return szFac * dMin;
}

float TrObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    p = ro + dHit * rd;
    d = TrObjDf (p);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float PlateCol (vec2 s)
{
  float d;
  if (s.x < -0.55) {
    s.x += 1.3;
    d = min (PrBox2Df (s, vec2 (0.06, 0.6)), max (abs (PrCaps2Df (s.yx, 0.55, 0.1)) -
       0.06, - s.x));
  } else if (s.x < 0.65) {
    d = min (PrBox2Df (s, vec2 (0.06, 0.6)), max (abs (PrCaps2Df ((s - vec2 (0.07, 0.3)).yx,
       0.25, 0.2)) - 0.06, - s.x));
    s -= vec2 (0.3, -0.3);
    d = min (d, max (PrBox2Df (Rot2D (s, - pi / 5.), vec2 (0.06, 0.4)), -0.3 - s.y));
  } else {
    d = PrBox2Df (vec2 (abs (s.x - 1.4) - 0.2, s.y), vec2 (0.06, 0.6));
  }
  return d;
}

float MSymCol (vec2 s)
{
  return min (abs (length (s) - 0.1) - 0.02, PrBox2Df (Rot2D (s, 2. * pi *
     floor (3. * atan (s.y, - s.x) / (2. * pi) + 0.5) / 3.) + vec2 (0.05, 0.),
     vec2 (0.05, 0.02)));
}

vec4 CarCol (out float refFac)
{
  vec4 col4, col4B, col4Ls, col4Ds, col4Uh, col4Ly, col4HiRf;
  vec2 s;
  float t;
  bool loRf;
  col4B = vec4 (0.8, 0.6, 0.1, 0.2);
  col4Ls = vec4 (0.9, 0.9, 0.9, 0.2);
  col4Ds = vec4 (0.4, 0.4, 0.4, 0.2);
  col4Uh = vec4 (0.7, 0.7, 0.5, 0.05);
  col4Ly = vec4 (0.9, 0.9, 0.8, 0.2);
  col4HiRf = vec4 (0.95, 0.95, 1., 0.);
  loRf = false;
  if (idObj <= idDash) {
    if (idObj == idBod) {
      if (dInt < 0.03) {
        col4 = 0.7 * col4Uh;
        loRf = false;
      } else {
        col4 = col4B;
        loRf = true;
      }
      if (qHit.z > 2.905) {
        col4 = col4HiRf;
        if (PrRoundBox2Df (vec2 (abs (qHit.x) - 0.28, qHit.y - 0.6), vec2 (0.05, 0.4), 0.15) < 0.) {
          col4 *= 0.7;
        }
        if (length (qHit.xy + vec2 (0., 0.07)) < 0.04) col4 *= 0.3;
      } else if (abs (qHit.z + 0.1 * qHit.y - 2.) < 0.8 && abs (abs (qHit.y - 0.7) - 0.15) < 0.07) {
        col4 = col4Ds;
        loRf = false;
      } else if (qHit.y > 0.) {
        t = (0.4 + 0.6 * smoothstep (0.005, 0.015, abs (PrRoundBox2Df (vec2 (qHit.y - 1.,
           qHit.z + 0.07 * qHit.y + 0.35), vec2 (0.7, 0.9 - 0.07 * qHit.y), 0.2)))) *
           (0.7 + 0.3 * smoothstep (0.005, 0.015, abs (PrRoundBox2Df (vec2 (qHit.y - 1.1,
           qHit.z - 1.95), vec2 (0.85, 0.92), 0.02))));
        if (abs (qHit.z - 1.95) < 0.93) t *= 0.7 + 0.3 * smoothstep (0.005, 0.015, abs (qHit.x));
        col4 *= t;
        if (t < 1.) loRf = false;
        if (abs (qHit.x) > 0.95) {
          t = PrRoundBox2Df (vec2 (abs (qHit.y - 0.7) - 0.25, qHit.z + 1.45), vec2 (0.1, 0.01), 0.01);
          col4 = mix (col4Ds, col4, smoothstep (0., 0.01, t));
          if (t < 0.) loRf = false;
        }
      }
      if (qHit.z < -1.8) col4 = mix (col4HiRf, col4, smoothstep (0., 0.02,
         PrRoundBox2Df (vec2 (abs (qHit.x) - 0.8, qHit.z + 2.5), vec2 (0.01, 0.6), 0.02)));
    } else if (idObj == idTyr) {
      t = length (qHit.xy);
      if (t < 0.53) {
        col4 = col4Ls * (0.5 + 0.5 * step (0., qHit.z));
      } else {
        col4 = vec4 (0.15, 0.15, 0.15, 0.);
        if (t > 0.75) col4 *= 0.5 + 0.5 * abs (step (0., cos (32. * pi * qHit.z)) -
           step (0.5, mod (64. * atan (qHit.y, - qHit.x) / (2. * pi) + 0.5, 1.)));
      }
    } else if (idObj == idHub) {
      t = length (qHit.xy);
      col4 = mix (col4Ds, col4HiRf, smoothstep (0., 0.002, abs (t - 0.17) - 0.01));
      if (qHit.z > 0.) {
        if (t < 0.15) col4 = mix (col4Ds, col4, smoothstep (0., 0.005, MSymCol (qHit.xy)));
      } else col4 *= 0.3;
      if (abs (t - 0.36) < 0.15) {
        t = 1.;
        for (float f = -1.; f <= 1.; f += 2.) {
          s = Rot2D (qHit.xy, f * pi / 24.);
          t = min (t, smoothstep (0.008, 0.01, abs (abs (dot (s, sin (2. * pi *
             floor (12. * atan (s.y, - s.x) / (2. * pi) + 0.5) / 12. +
             vec2 (0., 0.5 * pi)))) - 0.04)));
        }
        col4 = mix (col4Ls, 0.5 * col4B, t) * (0.5 + 0.5 * step (0., qHit.z));
      }
    } else if (idObj == idDash) {
      col4 = mix (col4B, 0.8 * col4Uh, smoothstep (0.015, 0.02, abs (abs (qHit.y) - 0.2)));
      col4 = mix (col4B, col4, smoothstep (0.015, 0.02, abs (PrRoundBox2Df (vec2 (qHit.x + 0.4,
         qHit.y), vec2 (0.2, 0.1), 0.03))));
      if (qHit.y < 0.22) {
        s = vec2 (abs (qHit.x - 0.42) - 0.25, qHit.y);
        t = length (s);
        if (t < 0.15) {
          col4 = vec4 (1., 1., 1., 0.1);
          if (t > 0.13) col4 *= 0.2;
          else {
            if (t > 0.08) col4 *= 0.2 + 0.8 * smoothstep (0.2, 0.25,
               mod (8. * atan (s.y, - s.x) / (2. * pi), 1.));
            if (s.y > -0.02) col4 *= 0.2 + 0.8 * smoothstep (0.012, 0.015, abs (s.x));
          }
        }
      }
    }
  } else if (idObj <= idPlat) {
    if (idObj == idWarch) {
      col4 = col4B * (0.5 + 0.5 * step (0., dWarch));
      loRf = (dWarch > 0. && (qHit.x > -0.3 || qHit.y > 0.5));
    } else if (idObj == idBase) {
      col4 = col4B * (0.9 + 0.1 * step (0., qHit.y) * (cos (64. * qHit.x) - 1.) *
         step (abs (qHit.z), 2.));
    } else if (idObj == idExR) {
      col4 = (qHit.z < - 1.2) ? col4HiRf : col4Ds;
      if (qHit.z < 1.3) col4 *= 0.1 + 0.9 * step (0.07, length (qHit.xy));
    } else if (idObj == idExF) {
      col4 = mix (col4Ds, col4HiRf, step (-0.4, sin (48. * atan (qHit.y, - qHit.x))));
    } else if (idObj == idWscrn) {
      col4 = col4HiRf;
    } else if (idObj == idSeat) {
      col4 = col4Uh * (0.95 + 0.05 * cos (64. * qHit.x));
    } else if (idObj == idStWhl) {
      col4 = mix (col4Ds, col4Ls, smoothstep (0., 0.004, MSymCol (Rot2D (1.5 * qHit.xy,
         - pi / 6. - 8. * strRot))));
    } else if (idObj == idPlat) {
      col4 = mix (0.5 * col4B, vec4 (1., 1., 1., 0.1), step (0., qHit.z) * 
         (1. - smoothstep (0.02, 0.03, PlateCol (7. * qHit.xy * vec2 (- pSign, 1.)))));
    }
  } else {
    if (idObj == idMLit) {
      col4 = col4HiRf;
      if (abs (qHit.z - 0.05) < 0.02) col4 *= 0.8;;
    } else if (idObj == idCLit) {
      col4 = col4HiRf;
      t = length (qHit.xy);
      if (qHit.z > 0. && t < 0.12) {
        col4 = col4Ly * (0.9 + 0.1 * cos (256. * t));
        loRf = true;
      }
    } else if (idObj == idSLit) {
      col4 = (qHit.z > 0. && length (qHit.xy) < 0.08) ? col4Ly : col4HiRf;
    } else if (idObj == idRLit) {
      col4 = (qHit.z < 0. && length (qHit.xy) < 0.05) ? vec4 (1., 0., 0., 0.2) : col4HiRf;
    } else if (idObj == idAx) {
      col4 = 0.7 * col4Ds;
    } else if (idObj == idShin) {
      col4 = col4HiRf;
    } else if (idObj == idLtSil) {
      col4 = col4Ls;
    } else if (idObj == idDkSil) {
      col4 = col4Ds;
    }
  }
  refFac = 0.;
  if (col4 == col4HiRf) refFac = 0.8;
  else if (loRf) refFac = 0.15;
  return col4;
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  d = max (PrRoundBoxDf (q, vec3 (pgSize + vec3 (1., 0.1, 1.)), 0.03),
     - PrBox2Df (q.xz, pgSize.xz));
  DMINQ (0);
  return dMin;
}

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec2 ExBlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, fcBlk;
  vec2 qBlk;
  float dn, df, bSize;
  bSize = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk)) / bSize;
  } else qBlk = vec2 (0.);
  return qBlk;
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fcBlk;
  vec2 qBlk;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return vec4 (dMin, fcBlk);
}

vec3 SkShowScene (vec3 ro, vec3 rd, vec2 uv)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, refFac;
  carPos = vec3 (0.);
  carRot = 0.;
  strRot = 0.;
  wlRot = vec2 (0.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = CarCol (refFac);
    col = vec3 (1., 0.98, 0.96) * pow (Maxv3 (col4.rgb), 0.7);
    col *= (0.85 + 0.15 * Noisefv2 (Rot2D (uv, -0.2 * pi) * vec2 (16., 256.))) *
       (0.5 + 0.5 * max (dot (vn, ltDirS), 0.));
  } else {
    col = vec3 (1.);
  }
  return clamp (col, 0., 1.);
}

vec3 SkMain (vec2 fCoord, float vp)
{
  mat3 vuMat;
  vec3 ro, rd, col, carPosT;
  vec2 uv, wlRotT;
  float el, az, szFacT, carRotT, strRotT;
  szFacT = szFac;
  carPosT = carPos;
  carRotT = carRot;
  wlRotT = wlRot;
  strRotT = strRot;
  uv = fCoord / 5.;
  if (vp == 0.) {
    szFac = 1.2;
    uv -= vec2 (0., -0.9);
    el = -0.2 * pi;
    az = pi;
  } else if (vp == 1.) {
    szFac = 1.3;
    uv -= vec2 (0.6, -1.5);
    el = -0.13 * pi;
    az = 0.5 * pi;
  } else if (vp == 2.) {
    szFac = 1.3;
    uv -= vec2 (-0.6, -1.2);
    el = -0.5 * pi;
    az = -0.5 * pi;
  } else if (vp == 3.) {
    szFac = 1.6;
    uv -= vec2 (0., -1.8);
    el = -0.2 * pi;
    az = 0.;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -5., -20.);
  rd = vuMat * normalize (vec3 (uv, 4.2));
  ltDirS = vuMat * normalize (vec3 (0.5, 1., -0.7));
  col = SkShowScene (ro, rd, uv);
  szFac = szFacT;
  carPos = carPosT;
  carRot = carRotT;
  wlRot = wlRotT;
  strRot = strRotT;
  return clamp (col, 0., 1.);
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff;
  rd.y = abs (rd.y);
  q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.2, 0.8, ff);
  fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * ltDir.xz)) - f;
  clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  skCol = vec3 (0.3, 0.4, 0.8);
  col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, db4;
  vec3 col, vn, qHitEx, roo;
  vec2 qBlk, q, sq;
  float dstObj, dstTrObj, dstExObj, refFac;
  bool isBg;
  szFac = 1.5;
  roo = ro;
  dstTrObj = TrObjRay (ro, rd);
  pgSize = vec3 (15., 0.1, 10.);
  isBg = false;
  dstExObj = ExObjRay (ro, rd);
  qHitEx = qHit;
  isSk = false;
  dstObj = ObjRay (ro, rd);
  db4 = BlkHit (ro, rd, pgSize);
  if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
    if (dstObj < dstExObj) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      col4 = CarCol (refFac);
    } else {
      ro += dstExObj * rd;
      vn = ExObjNf (ro);
      col4 = vec4 (0.4, 0.5, 0.1, 0.1) * (0.8 + 0.2 * Fbm2 (16. * qHitEx.xz));
    }
  } else if (db4.x < dstFar) {
    ro += db4.x * rd;
    vn = db4.yzw;
    q = ro.xz;
    if (vn.y > 0.5 && PrBox2Df (q, pgSize.xz - 0.96) < 0.) {
      sq = sign (q);
      isSk = true;
      col = SkMain (q - 0.5 * sq * pgSize.xz,  sq.y + 1. + 0.5 * (sq.x + 1.));
      if (Minv3 (col) > 0.95) col = mix (vec3 (0.7, 0.8, 0.9), vec3 (1.),
         Minv2 (smoothstep (0.02, 0.05, abs (mod (q + 0.5, 1.) - 0.5))));
    } else {
      col = vec3 (1.) * (0.8 + 0.2 * step (0.5, vn.y));
    }
    col *= 0.7 + 0.3 * smoothstep (0., 0.5, PrRoundBox2Df (Rot2D (q - szFac * carPos.xz,
       carRot) - vec2 (0., szFac * wlBase.z - 0.4), szFac * wlBase.xz + vec2 (-0.5, 1.), 0.5));
  } else {
    isBg = true;
  }
  if (! isBg) {
    if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
      if (refFac > 0.) col = mix (col, 0.9 * SkyBgCol (ro, reflect (rd, vn)), refFac);
    } else {
      col *= 0.3 + 0.7 * max (dot (vn, ltDir), 0.);
    }
  } else {
    qBlk = mod (8. * ExBlkHit (ro, rd), 1.);
    col = vec3 (0.4, 0.5, 0.4) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.)) +
       vec3 (0.2) * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.1, qBlk.x) *
       SmoothBump (0.25, 0.75, 0.1, qBlk.y));
  }

  if (dstTrObj < min (dstObj, dstFar)) {
    ro = roo + dstTrObj * rd;
    vn = TrObjNf (ro);
    if (idTrObj == idWscrn) col *= vec3 (1., 1., 0.8);
    else if (idTrObj == idMLit) col *= vec3 (1., 1., 0.8) * (0.9 + 0.1 * cos (128. * qTrHit.x)); 
    col = mix (col, SkyBgCol (ro, reflect (rd, vn)), 0.1 + 0.8 * pow (1. - abs (dot (vn, rd)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  stDat = Loadv4 (0);
  carPos = stDat.xyz;
  carRot = stDat.w;
  stDat = Loadv4 (1);
  wlBase = stDat.xyz;
  wlRad = wlBase.y;
  spd = stDat.w;
  stDat = Loadv4 (2);
  strRot = stDat.y;
  wlRot = stDat.zw;
  az = 0.;
  el = -0.3 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.8 * pi * mPtr.y;
  }
  el = clamp (el, -0.5 * pi, -0.1 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -1., -40.);
  rd = vuMat * normalize (vec3 (uv, 4.));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float PrFlatCapsDf (vec3 p, float r, float h)
{
  return length (vec2 (max (length (p.xy) - r, 0.), p.z)) - h;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float PrTorusBxDf (vec3 p, vec3 b, float ri)
{
  return length (vec2 (length (max (abs (p.xy) - b.xy, 0.)) - b.z, p.z)) - ri;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

