const float pi = 3.1415927;
const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat;
  vec3 carPos, wlBase;
  vec2 iFrag, wlRot, w;
  float carRot, cRotN, strRot, wlRad, rTurn, spd, tc, nStep;
  int pxId;
  bool init;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (pxId >= 3) discard;
  init = (iFrame <= 5);
  if (init) {
    carPos = vec3 (8.5, 0., -2.5);
    carRot = -0.4 * pi;
    strRot = 0.;
    wlBase = vec3 (1.45, 0.8, 2.7);
    wlRot = vec2 (0.);
    nStep = 0.;
  } else {
    stDat = Loadv4 (0);
    carPos = stDat.xyz;
    carRot = stDat.w;
    stDat = Loadv4 (1);
    wlBase = stDat.xyz;
    stDat = Loadv4 (2);
    nStep = stDat.x;
    strRot = stDat.y;
    wlRot = stDat.zw;
  }
  wlRad = wlBase.y;
  ++ nStep;
  tc = mod (0.002 * nStep, 4.);
  strRot = 0.15 * pi * SmoothBump (0.3, 0.7, 0.15, mod (tc, 1.)) * sign (mod (tc, 2.) - 1.) *
     sign (tc - 2.);
  tc = mod (0.003 * nStep, 2.);
  spd = -0.03 * SmoothBump (0.2, 0.8, 0.15, mod (tc, 1.)) * sign (tc - 1.);
  w = vec2 (1.);
  if (abs (strRot) > 1e-4) {
    cRotN = carRot - strRot * spd / pi;
    rTurn = wlBase.z / asin (0.5 * strRot);
    carPos.xz += rTurn * (sin (carRot - vec2 (0.5 * pi, 0.)) - sin (cRotN - vec2 (0.5 * pi, 0.)));
    carRot = cRotN;
    w += vec2 (-1., 1.) * wlBase.x / rTurn;
  } else {
    carPos.xz += spd * sin (carRot + vec2 (0., 0.5 * pi));
  }
  wlRot += w * spd / wlRad;
  if      (pxId == 0) stDat = vec4 (carPos, carRot);
  else if (pxId == 1) stDat = vec4 (wlBase, spd);
  else if (pxId == 2) stDat = vec4 (nStep, strRot, wlRot);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
