#if HW_PERFORMANCE!=0
// If defined, activate anti-aliasing, level: 2 or 3
#define AA 2
#endif

const float AXIS_VELO = 1.0 / PI;
const float ROTATE_VELO = 1.0;

// https://iquilezles.org/articles/distfunctions
float sdRoundBox(vec3 p, vec3 b, float r) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

// Random unit vector
// https://qiita.com/aa_debdeb/items/e416ae8a018692fc07eb
vec3 randomAxis(vec2 gen) {
    vec2 uv = hash22(gen);
    float z = 2.0 * uv.x - 1.0;
    float t = 2.0 * PI * uv.y;
    return vec3(
        sqrt(1.0 - z * z) * cos(t),
        sqrt(1.0 - z * z) * sin(t),
        z
    );
}

// cellwise SDF
float cellDist(in vec3 p, in vec3 q, in float t) {
    vec3 fq = floor(q);
    float gen = fq.x + fq.y * sqrt(2.0) + fq.z * sqrt(5.0);
    float eGen0 = hash11(gen + 0.8649 + floor(t / 2.0));
    float eGen1 = hash11(gen + 1.8649 + floor(t / 2.0));
    float eGen2 = hash11(gen + 2.8649 + floor(t / 2.0));
    float eGen3 = hash11(gen + 3.8649 + floor(t / 2.0));
    float eGen4 = hash11(gen + 4.8649 + floor(t / 2.0));
    bool prevExists = (eGen0 + eGen1 + eGen2) / 3.0 < 0.75;
    bool corrExists = (eGen1 + eGen2 + eGen3) / 3.0 < 0.75;
    bool nextExists = (eGen2 + eGen3 + eGen4) / 3.0 < 0.75;
    if (corrExists) return 0.5;
    vec3 disp = 0.1 * hash33(fq);
    p = fract(q) + p - q - 0.5 - disp;
    vec3 axis = randomAxis(fq.xy);
    float a = 1.0;
    float k = 5.0;
    float m = 1.2;
    float s = fract(t * 0.5);
    float tmp = m - m * k * k * (s - 1.0 / k) * (s - 1.0 / k);
    tmp = prevExists ? tmp : a;
    a = s < 1.0 / k ? tmp : max(tmp, a);
    tmp = m - m * k * k * (s - (k - 1.0) / k) * (s - (k - 1.0) / k);
    tmp = nextExists ? tmp : a;
    a = s > (k - 1.0) / k ? tmp : max(tmp, a);
    p = rot(axis, ROTATE_VELO * t) * p;
    return sdRoundBox(p, vec3(0.15) * a, 0.025 * a);
}

// SDF
float sDist(in vec3 p, in vec3 dir, in float t) {
    float dist = cellDist(p, p, t);
    if (floor(p + dist * dir) != floor(p)) {
        dist = min(dist, cellDist(p, p + dist * dir, t));
    }
    return dist;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p, in vec3 dir, in float t) {
    const float eps = 0.0001;
    const vec2 h = vec2(eps,0);
    return normalize(vec3(
        cellDist(p+h.xyy, p, t) - cellDist(p-h.xyy, p, t),
        cellDist(p+h.yxy, p, t) - cellDist(p-h.yxy, p, t),
        cellDist(p+h.yyx, p, t) - cellDist(p-h.yyx, p, t)
    ));
}

const float FAR = 12.0;
void mainImage0(out vec4 fragColor, in vec2 fragCoord) {
    vec2 theta = vec2(0.15, 0.25) * iTime;
    theta.y = sin(theta.y) * 0.7;
    Camera camera = newCamera(
        vec3(sin(iTime * 0.1) * 0.1, -iTime * 0.5, cos(iTime * 0.1) * 0.1),
        vec3(cos(theta.x) * cos(theta.y), sin(theta.y), sin(theta.x) * cos(theta.y)),
        //vec3(0),
        //vec3(0, 0, 1),
        vec3(0, 1, 0),
        PI / 4.0,
        iResolution.x / iResolution.y
    );
    Ray ray = cameraRay(camera, fragCoord / iResolution.xy);

    vec3 p = ray.origin;
    for (int _i = 0; _i < 100; _i++) {
        float dist = sDist(p, ray.direction, iTime);
        float rDist2 = dot(p - ray.origin, p - ray.origin);
        if (dist < 0.00001 || rDist2 > 144.0) break;
        p += dist * ray.direction;
    }

    vec3 col = vec3(0.9, 0.8, 0.9);
    float dist = length(p - ray.origin);
    if (dist < FAR) {
        vec3 normal = calcNormal(p, ray.direction, iTime);
        float c = -dot(ray.direction, normal);
        c = clamp(c, 0.0, 1.0);
        float k = smoothstep(0.0, 1.0, dist / FAR);
        k *= k;
        vec3 mat = hash33(floor(p) + 1.23);
        col = (1.0 - k) * c * mat + k * col;
    }

    fragColor = vec4(pow(col, vec3(0.4545)), 1);
}

void mainImage(out vec4 O, vec2 U) {
    mainImage0(O,U);

// smart anti-aliasing
// reference: https://shadertoyunofficial.wordpress.com/2021/03/09/advanced-tricks/

// If defined, visualize anti-aliased pixels.
//#define VISUALIZE_AA
#ifdef AA
    if (fwidth(length(O.xyz)) > 0.2) {
        O.xyz = pow(O.xyz, vec3(2.2));
        vec4 o;
#if (AA == 2)
        for (int k = 0; k < 4; k++) {
              mainImage0(o,U + (vec2(k % 2, k / 2) - 0.5) / 1.5);
              O += pow(o, vec4(2.2));
        }
        O /= 5.0;
#elif (AA == 3)
        for (int k = 0; k < 9; k += k == 3 ? 2 : 1 ) {
              mainImage0(o, U + vec2(k % 3 - 1, k / 3 - 1) / 3.0);
              O += pow(o, vec4(2.2));
        }
        O /= 9.0;
#endif
#ifdef VISUALIZE_AA
        O = vec4(1);
    } else {
        O = vec4(0,0,0,1);
#endif
        O.xyz = pow(O.xyz, vec3(0.4545));
    }
#endif
    O.w = 1.0;
}

const float PI = 3.141592653;

struct Camera {
    vec3 position;
    vec3 direction;
    vec3 up_direction; // not require dot(direction, up_direction) == 0
    float fov;
    float aspect; // x / y
};

struct Ray {
    vec3 origin;
    vec3 direction;
};

Camera newCamera(vec3 position, vec3 direction, vec3 up_direction, float fov, float aspect) {
    Camera camera;
    camera.position = position;
    camera.direction = direction;
    camera.up_direction = up_direction;
    camera.fov = fov;
    camera.aspect = aspect;
    return camera;
}

// perspective camera ray, uv = fragCoord / iResolution.xy
// cf: https://qiita.com/aa_debdeb/items/301dfc54788f1219b554
Ray cameraRay(in Camera camera, in vec2 uv) {
    uv = uv * 2.0 - 1.0;
    float h = tan(camera.fov * 0.5);
    float w = h * camera.aspect;
    vec3 right = normalize(cross(camera.direction, camera.up_direction));
    vec3 up = normalize(cross(right, camera.direction));
    vec3 direction = normalize(right * w * uv.x + up * h * uv.y + camera.direction);
    Ray ray;
    ray.origin = camera.position;
    ray.direction = direction;
    return ray;
}

// Rodrigues' rotation formula
mat3 rot(vec3 axis, float angle) {
    return mat3(
        axis[0] * axis[0] * (1.0 - cos(angle)) + cos(angle),
        axis[0] * axis[1] * (1.0 - cos(angle)) + axis[2] * sin(angle),
        axis[0] * axis[2] * (1.0 - cos(angle)) - axis[1] * sin(angle),
        axis[0] * axis[1] * (1.0 - cos(angle)) - axis[2] * sin(angle),
        axis[1] * axis[1] * (1.0 - cos(angle)) + cos(angle),
        axis[1] * axis[2] * (1.0 - cos(angle)) + axis[0] * sin(angle),
        axis[0] * axis[2] * (1.0 - cos(angle)) + axis[1] * sin(angle),
        axis[1] * axis[2] * (1.0 - cos(angle)) - axis[0] * sin(angle),
        axis[2] * axis[2] * (1.0 - cos(angle)) + cos(angle)
    );
}


/* Hash without Sine https://www.shadertoy.com/view/4djSRW */
float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec2 hash22(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}
