float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}
float Smile(vec2 uv, vec3 col, vec2 position) {
    float mask = Circle(uv, position, 0.3, 0.003);
    mask -= Circle(uv, position-vec2(-0.1, -0.08), 0.05, 0.003);

    float leftEye = Circle(uv, position-vec2(0.1, -0.07), 0.05, 0.003);
    leftEye -= Circle(uv, position-vec2(0.1, -0.06), 0.05, 0.003);
    col *= leftEye;
    mask -= leftEye;

    float smile = Circle(uv, position-vec2(0,0), 0.02, 0.003);
    smile -= Circle(uv, position-vec2(0,-0.02), 0.02, 0.003);
    col *= smile;
    mask -= smile;
    return mask;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.4, 0.5, 0.5);
    col /= 0.6 + 0.4*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 position = vec2(0,0);

    position += vec2(0.5,0.5);
    position *= cos(iTime/uv.x)*uv.x*0.1;

    float mask = Smile(uv, col, position);
    vec3 col2 = col*mask;
    col -= col2;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
