#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURFACE_DIST 0.01
#define AA 1
float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

vec2 rand2(vec2 p)
{
	vec2 q = vec2(dot(p,vec2(127.1,311.7)), 
		dot(p,vec2(269.5,183.3)));
	return fract(sin(q)*43758.5453);
}

float rand(vec2 p)
{
	return fract(sin(dot(p,vec2(419.2,371.9))) * 833458.57832);
}

float iqnoise(in vec2 pos, float irregular, float smoothness)
{
	vec2 cell = floor(pos);
	vec2 cellOffset = fract(pos);

	float sharpness = 1.0 + 63.0 * pow(1.0-smoothness, 4.0);

	float value = 0.0;
	float accum = 0.0;
	// Sample the surrounding cells, from -2 to +2
	// This is necessary for the smoothing as well as the irregular grid.
	for(int x=-2; x<=2; x++ )
	for(int y=-2; y<=2; y++ )
	{
		vec2 samplePos = vec2(float(y), float(x));

  		// Center of the cell is not at the center of the block for irregular noise.
  		// Note that all the coordinates are in "block"-space, 0 is the current block, 1 is one block further, etc
		vec2 center = rand2(cell + samplePos) * irregular;
		float centerDistance = length(samplePos - cellOffset + center);

		// High sharpness = Only extreme values = Hard borders = 64
		// Low sharpness = No extreme values = Soft borders = 1
		float sam = pow(1.0 - smoothstep(0.0, 1.414, centerDistance), sharpness);

		// A different "color" (shade of gray) for each cell
		float color = rand(cell + samplePos);
		value += color * sam;
		accum += sam;
	}

	return value/accum;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    //line from a to b
    vec3 ab = b-a;

    // line from p to a
    vec3 ap = p-a;

    // as p approaches a, it will be 1
    // as p approaches b it will b 0
    float t = dot(ab,ap) / dot(ab,ab);

    // clamp values between 0 and 1
    t= clamp(t,0.,1.);

    // center point of the capsule
    // start at a (top of capsule)
    // go towards b, t steps
    vec3 c = a + t*ab;

    float d = length(p-c)-r;

    return d;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.));
}
float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
  p = abs(p);
  vec2 b = vec2(la,lb);
  float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
  vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
  return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}
vec2 colMin(vec2 res, vec2 obj){
    res.x = min(obj.x,res.x);
    if(obj.x == res.x){
        res.y = obj.y;
    }
    return res;
}
// https://iquilezles.org/articles/smin
vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// https://iquilezles.org/articles/smin
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}
float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}
float getDisp(vec2 uv){

    vec2 p = 0.5 - 0.5*sin(vec2(1.01,1.71) );
    p = vec2(1.0, 1.0);

	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);

	float f = iqnoise(24.0*uv, p.x, p.y);

	return f;
}
vec2 GetDist(vec3 p){
    vec2 res = vec2(p.y+.3,1.);
    vec2 sphere = vec2(length(p-vec3(0,1,-2))-.4,2.);
    res = colMin(res, sphere);
    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            dO.y= -1.;
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,5,-6);

    lightPos.xz += vec2(sin(iTime),cos(iTime));
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= .1;
    }

    return diff;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 mo = iMouse.xy/iResolution.xy;



    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ta = vec3( 0, 0., -3);
    vec3 ro = ta + vec3( 4.5, 4.3, 4.5);
    mat3 ca = setCamera( ro, ta, 0.0 );
//    vec3 ro = vec3(0,3,-5);


    //ray dir
    //controls rotation
    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 pt = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 pt = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif
    vec3 rd = ca * normalize( vec3(pt,2.5) );
    //------------------
    vec2 d = RayMarch(ro,rd);

    // position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d.x;

    //get diffuse lighting
    float diff = GetLight(p);
    vec3 col = vec3(0);
    col = vec3(diff);

    vec3 n = GetNormal(p);

    if(d.y == 1.){
        col *= vec3(1,0,0);
        vec3 colXZ = texture(iChannel0, p.xz*.5+.5).rgb;
    vec3 colXY = texture(iChannel0, p.xy*.5+.5).rgb;
    vec3 colYZ = texture(iChannel0, p.yz*.5+.5).rgb;
    vec3 shadow = colXZ*abs(n.y) + colXY*abs(n.z) + colYZ*abs(n.x);
    col = max(smoothstep(.5,.1,diff)*shadow,col);
    }
    else if(d.y==2.){
       col *= vec3(0,1,0);
    }
    else if(d.y==3.){
        col=vec3(1,0,0);
    }
    else if(d.y==4.){
      //  col*=decCol(p, n, rd)*vec3(1,1,0);
    }

    fragColor = vec4(col,1.0);
}
