// License CC0: Beat boxing II
//  Took the beat boxing shader from yesterday and kicked it up a notch: https://www.shadertoy.com/view/7tGcRV
//  Based on Evilryus version of mandelbox:
//   https://www.shadertoy.com/view/XdlSD4
//  Music by Ultrasyd (sadly no longer with us): https://soundcloud.com/ultrasyd/shitstep-game-boy
//   The music AFAIK is made on a Gameboy!

// If no sound it is usually because browsers block audio element that has no interaction
//  So tinker with the player controls and the browser might decide to start the audio

// Oh yeah and if no music then shader is black

//#define THEME0
#define THEME1

#define TOLERANCE       0.001
#define NORMTOL         0.00125
#define MAX_RAY_LENGTH  10.0
#define MAX_RAY_MARCHES 90
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define PI              3.141592654
#define TAU             (2.0*PI)

// https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

mat2 g_rot = ROT(0.0);

float g_quad = 0.0;
float g_beat = 0.0;
float g_drum = 0.0;

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/spherefunctions/spherefunctions.htm
float sphered(vec3 ro, vec3 rd, vec4 sph, float dbuffer) {
  float ndbuffer = dbuffer/sph.w;
  vec3  rc = (ro - sph.xyz)/sph.w;

  float b = dot(rd,rc);
  float c = dot(rc,rc) - 1.0;
  float h = b*b - c;
  if( h<0.0 ) return 0.0;
  h = sqrt( h );
  float t1 = -b - h;
  float t2 = -b + h;

  if( t2<0.0 || t1>ndbuffer ) return 0.0;
  t1 = max( t1, 0.0 );
  t2 = min( t2, ndbuffer );

  float i1 = -(c*t1 + b*t1*t1 + t1*t1*t1/3.0);
  float i2 = -(c*t2 + b*t2*t2 + t2*t2*t2/3.0);
  return (i2-i1)*(3.0/4.0);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mårten Rånge, found: https://github.com/mrange/glsl-snippets
float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float hexagon(vec2 p, float r) {
  const vec3 k = 0.5*vec3(-sqrt(3.0), 1.0, sqrt(4.0/3.0));
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

// License: Unknown, author: Martijn Steinrucken, found: https://www.youtube.com/watch?v=VmrIDyYiJBA
vec2 hextile(inout vec2 p) {
  // See Art of Code: Hexagonal Tiling Explained!
  // https://www.youtube.com/watch?v=VmrIDyYiJBA
  const vec2 sz       = vec2(1.0, sqrt(3.0));
  const vec2 hsz      = 0.5*sz;

  vec2 p1 = mod(p, sz)-hsz;
  vec2 p2 = mod(p - hsz, sz)-hsz;
  vec2 p3 = dot(p1, p1) < dot(p2, p2) ? p1 : p2;
  vec2 n = ((p3 - p + hsz)/sz);
  p = p3;

  n -= vec2(0.5);
  // Rounding to make hextile 0,0 well behaved
  return round(n*2.0)*0.5;
}

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}


// -------------------------------------------------
// Mandelbox - https://www.shadertoy.com/view/XdlSD4

const float fixed_radius2 = 1.9;
const float min_radius2 = 0.1;
const float folding_limit = 1.0;
const float scale = -2.5;


void sphere_fold(inout vec3 z, inout float dz) {
  float r2 = dot(z, z);
  if(r2 < min_radius2) {
    float temp = (fixed_radius2 / min_radius2);
    z *= temp;
    dz *= temp;
  } else if(r2 < fixed_radius2) {
    float temp = (fixed_radius2 / r2);
    z *= temp;
    dz *= temp;
  }
}

void box_fold(inout vec3 z, inout float dz) {
  z = clamp(z, -folding_limit, folding_limit) * 2.0 - z;
}

float mb(vec3 z) {
  float dd = length(z)-g_drum;
  vec3 offset = z;
  float dr = 1.0;

  for(int n = 0; n < 5; ++n) {
    box_fold(z, dr);
    sphere_fold(z, dr);

    z = scale * z + offset;
    dr = dr * abs(scale) + 1.5;
  }

  float d = (length(z))/abs(dr)-0.04;
  dd = pmax(dd, -d, 0.5);
  if (dd < d) {
    g_quad = -1.0;
  } else {
    g_quad = 1.0;
  }
  g_quad = -g_quad;
  d = min(d, dd);

  return d;
}
// -------------------------------------------------


float df(vec3 p) {
  p.xz *= g_rot;
  const float z = 1.0;
  float d = mb(p/z)*z;

  return d; 
} 

float rayMarch(in vec3 ro, in vec3 rd, out int iter) {
  float t = 2.0;
  int i = 0;
  for (i = 0; i < MAX_RAY_MARCHES; i++) {
    float d = df(ro + rd*t);
    if (d < TOLERANCE || t > MAX_RAY_LENGTH) break;
    t += d;
  }
  iter = i;
  return t;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORMTOL, 0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float softShadow(in vec3 pos, in vec3 ld, in float ll, float mint, float k) {
  const float minShadow = 0.25;
  float res = 1.0;
  float t = mint;
  for (int i=0; i<25; ++i) {
    float distance = df(pos + ld*t);
    res = min(res, k*distance/t);
    if (ll <= t) break;
    if(res <= minShadow) break;
    t += max(mint*0.2, distance);
  }
  return clamp(res,minShadow,1.0);
}

vec4 render(vec3 ro, vec3 rd) {
  const vec3 lightPos0  = 2.5*vec3(1.0, 1.0, 1.0);
  const vec3 lightPos1  = vec3(0.0);
  float beat = g_beat;
  int iter = 0;
  float t = rayMarch(ro, rd, iter);
  float quad = g_quad;

  float tt = float(iter)/float(MAX_RAY_MARCHES);
  float bs = 1.0-tt*tt*tt*tt;

  vec3 pos = ro + t*rd;    

  float lsd1  = sphered(ro, rd, vec4(lightPos1, mix(2.3, 2.4, beat)), t);

  const vec3 bcol0 = HSV2RGB(vec3(0.6, 0.6, 3.0));
  const vec3 bcol1 = HSV2RGB(vec3(0.55, 0.8, 7.0));
  vec3 bcol   = mix(bcol0, bcol1, beat);
  vec3 gcol   = lsd1*bcol;

  if (t >= MAX_RAY_LENGTH) {

    return vec4(gcol, sqrt(lsd1));
  }

  float d     = df(pos);  
  vec3 nor    = normal(pos);
  float fre   = 1.0+dot(nor, rd);
  fre *= fre;
  fre *= fre;

  vec3 lv0    = lightPos0 - pos;
  float ll20  = dot(lv0, lv0);
  float ll0   = sqrt(ll20);
  vec3 ld0    = lv0 / ll0;
  float dm0   = 8.0/ll20;
  float sha0  = softShadow(pos, ld0, ll0, 0.125, 32.0);
  float dif0  = max(dot(nor,ld0),0.0)*dm0;
  float spe0  = pow(max(dot(reflect(rd, nor), ld0), 0.), 10.0);


  vec3 col = vec3(0.0);
  quad = -quad;
  const vec3 black = vec3(0.0);
#if defined(THEME0)
  const vec3 dcol0 = HSV2RGB(vec3(0.6, 0.5, 1.0));
  const vec3 dcol1 = black;
#elif defined(THEME1)
  const vec3 dcol0 = HSV2RGB(vec3(0. , 0.75, -0.25));
  const vec3 dcol1 = HSV2RGB(vec3(0.08, 1.0, 1.0));
#else
  const vec3 dcol0 = black;
  const vec3 dcol1 = dcol0;
#endif
  col += dif0*dif0*sha0*mix(dcol0, dcol1, 0.5+0.5*quad);
  col += spe0*bcol*bs*sha0;
  col += gcol;

  return vec4(col, 1.0);
}

vec3 synth(vec2 cp, out vec3 gg) {
  float aaa = 2.0/RESOLUTION.y;
  cp -= vec2(-0.75*PI, -0.3);
  cp *= ROT(4.0*length(cp)*g_beat);
  vec2 tp = 40.0*cp;
  tp -= 10.0*TIME;
  float aa = length(fwidth(tp))*sqrt(0.5);

  vec2 hp = tp;
  vec2 hn = hextile(hp);
  float h0 = hash(hn+1234.5);
  float h1 = fract(8677.0*h0);
  float fft = texture(iChannel0, vec2(h1, 0.25)).x;
  fft = tanh_approx(2.0*max(fft-0.5, 0.0));
  fft *= fft;
  float d = max(length(hp)-0.25*fft, 0.0);
  vec3 bcol = hsv2rgb(vec3(h0, mix(0.95, 0.95, d*d), 0.25*fft));
  float hd = hexagon(hp.yx, 0.49);
  vec3 col = bcol/(d);
  col = min(col, vec3(10.0));

  col = mix(col, bcol*10.0, smoothstep(aa, -aa, -hd));
  const vec3 gcol = HSV2RGB(vec3(0.55, 0.9, 1.0));
  gg = gcol*25.0*(aaa*aaa)/(aa*aa);
  return col;
}

vec3 wave(vec2 cp) {
  vec3 col = vec3(0.0);
  for (int i = 0; i < 3; ++i) {
    vec3 scol = hsv2rgb(vec3(0.2*TIME+cp.x+0.0+0.2*float(i), 0.999, 0.005));
    float off = texture(iChannel0, vec2(0.15*(cp.x+3.0*float(i)+PI), 0.75)).x;

    float dd = max(abs(cp.y+0.3+0.2*off)-0.001, 0.0);
    col += abs(cp.x)*scol/(dd);
  }
  col = min(col, vec3(5.0, 2.5, 2.5));
  return col;
}

vec3 effect(vec2 p) {
  float fft = 0.0;
  fft += texture(iChannel0, vec2(0.3, 0.25)).x;
  fft += texture(iChannel0, vec2(0.2, 0.25)).x;
  fft += texture(iChannel0, vec2(0.1, 0.25)).x;
  fft /= 3.0;

  float beat = tanh_approx(8.0*max(fft-0.5, 0.0));
  beat *= beat;
  beat *= beat;
  g_beat = beat;
  g_drum = mix(2.05, 2.2, smoothstep(0.8, 1.0, beat));

  p *= ROT(-0.25*(beat)*(length(p)+0.1));
  g_rot = ROT(-0.1*TIME+0.1*beat);
  vec3 cam  = 5.0*vec3(1.0, 0.5, 1.0);
  vec3 dcam = normalize(vec3(0.0) - cam);


  vec3 ro = cam;
  vec3 ww = dcam;
  vec3 uu = normalize(cross(vec3(0.0,1.0,0.0), ww));
  vec3 vv = cross(ww,uu);
  float rdd = tan(TAU/mix(5.5, 5.0, beat*(0.5+0.8*length(p))));
  vec3 rd = normalize(-p.x*uu + p.y*vv + rdd*ww);

  float a = atan(rd.x, rd.z);
  vec2 cp = vec2(a, rd.y);


  vec3 col = vec3(0.0);
  vec3 gcol = vec3(0.0);
  col += synth(cp, gcol);
  col += wave(cp);
  vec4 cube = render(ro, rd);
  col = mix(col, cube.xyz, cube.w);
  col += gcol;
  col += smoothstep(0.9, 1.0, beat)*smoothstep(0.5, 3.0, length(p));
  col *= smoothstep(0.0, 0.3, beat);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect(p);
  col = aces_approx(col);
  col = sRGB(col);

  fragColor = vec4(col, 1.0);
}
