vec2 R; float T;
#define N 2.
vec2 o(vec2 v) {
    v.x = v.x + R.x*floor(N*v.y/R.y);
    v.y = mod(v.y,R.y/N);
    return v;
}
vec2 oo(vec2 v) {
    v = clamp(v,vec2(0),R*vec2(N,1./N)-1.);
    v.y = v.y + R.y/N*floor(v.x/R.x);
    v.x = mod(v.x,R.x);
    return v;
}
