    }

    if (U.y<1.) Q.w = 0.;
    if (U.y<4.) Q.y += .05;
    if (U.x<1.) Q.w = 0.;
    if (U.x<4.) Q.x += .05;
    if (R.y/N-U.y<1.) Q.w = 0.;
    if (R.y/N-U.y<5.) Q.y -= .05;
    if (R.x*N-U.x<2.) Q.w = 0.;
    if (R.x*N-U.x<4.) Q.x -= .05;
    if (U.y<(-.6*R.x+.5*U.x)/R.x/N*R.y-4.) Q.w *= 0.;
    if (U.y<(-.6*R.x+.5*U.x)/R.x/N*R.y) Q.y += .02,Q.x-=.05;

    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);

}

