void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = (fragCoord-.5*iMouse.xy)/iMouse.y;

    fragColor = vec4(perlin(uv*15.));
}

#define v01 vec2(0,1)
#define v10 vec2(1,0)
float r21(vec2 p){
    p = fract(p*vec2(233.65,987.56));
    p += dot(p,p+456.645);
    return fract(p.x*p.y);
}
vec2 r22(vec2 p){
    float n = r21(p);
    return vec2(n, r21(p +n));
}
vec2 toMinusOneOne(vec2 v){return v*2.-1.;}

float perlin(vec2 uv){
    vec2 id=floor(uv);
    vec2 gv=fract(uv);

    vec2 r00 = toMinusOneOne( r22(id) );
    vec2 r01 = toMinusOneOne( r22(id+v01) );
    vec2 r10 = toMinusOneOne( r22(id+v10) );
    vec2 r11 = toMinusOneOne( r22(id+1.) );

    vec2 g00 = gv;
    vec2 g01 = gv-v01;
    vec2 g10 = gv-v10;
    vec2 g11 = gv-1.;

    float d00 = dot(r00,g00);
    float d01 = dot(r01,g01);
    float d10 = dot(r10,g10);
    float d11 = dot(r11,g11);

    vec2 w = gv*gv*(3.-2.*gv);
    float x1 = mix(d00,d10,w.x);
    float x2 = mix(d01,d11,w.x);
    float x  = mix(x1, x2, w.y);
    x = x*.5+.5;

    return x;
}
