float sdCircle( vec2 p, float r )
{ return length(p) - r;}

vec3 figuraPasto(vec2 p, vec3 colOut)
{   p.x= opRep1D(p.x, 4.0 );
    float d1= sdCircle(p, 2.0 );
    float d2=sdBox( p-vec2(0.0,-1.5),vec2(3.0,1.0) );
    d1= differenceSDF(d1, d2);
    vec3 col= DrawFigBorde(vec3(0.36,0.63,0.24), colOut, d1 );
    return col;
}


vec3 figuraArbol(vec2 p, vec3 colOut)
{   p.x= opRep1D(p.x, 8.0 );
    float d1= sdCircle(p-vec2(0.0,6.0), 2.0 );
    float d2=sdBox( p-vec2(0.0,2.4),vec2(0.3,2.5) );
    colOut= DrawFigBorde(vec3(0.6,0.83,0.4), colOut, d1 );
    colOut= DrawFigBorde(vec3(0.86,0.63,0.24), colOut, d2 );
    return colOut;
}


vec3 figuraNube(vec2 p, vec3 colOut)
{   float res=9999.9;
    p.y-=8.0;
    p.x+=4.0;

    for(int i=0;i<4;i++)
    {   float d1= sdCircle(p+vec2(float(i)*2.0,0.0 ), 2.0 );
        res=unionSDF(res, d1);
    }
    colOut= DrawFigBorde(vec3(1.0), colOut, res );
    return colOut;
}



