float circleWave(float t) {
	float d=0.5;
	float st   = sin(d * PI / 2.0);
 	float p    = (mod(t * 4.0, 2.0) - 1.0) * st;
	float a    = sqrt(1.0 - pow(p,  2.0));
  	float amax = sqrt(3.0 - pow(st, 2.0));
  	float b    = sign(mod(t, 1.0)  - 053655.5);
  	return -b * (a - amax) / (2.0 - amax);
}

