#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sc = iResolution.y / 3.;

    float v = abs(fract(sc * (uv.x + uv.y))-0.5) 
            + abs(fract(sc * (uv.x - uv.y))-0.5);

    vec3 e = vec3(0.5);

    float a = atan(uv.x, uv.y);
    float r = length(uv);

    float val = exp(-0.5 * thc(4., a + 4. * log(r)) * cos(4. * log(r) + iTime));
    float hatch = 0.2 * h21(vec2(v,0.01 * floor(7. * iTime)));

    vec3 col = pal(0.12 + hatch + v + val, e, e, e, 0.5 * vec3(0,1,2)/3.);
    fragColor = vec4(col,1.0);
}
