float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }


//operacion de Union  por FabriceNeyret2
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )
#define opU(d1, d2) ( d1.x < d2.x ? d1 : d2 )


float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdCylinderXZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xz),p.y)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }

float sdCylinderXY( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xy),p.z)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }

float sdEllipsoid( vec3 p, vec3 r )
{  float k0 = length(p/r);  float k1 = length(p/(r*r));  return k0*(k0-1.0)/k1;}


float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{ vec3 pa = p - a, ba = b - a;   float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}
///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB) 
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}

// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z); }
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x); }
vec3 rotate_z(vec3 p, float phi) 
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z); }

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

//https://www.shadertoy.com/view/3tSBzW
mat3 rotX(float a) { float s = sin(a), c = cos(a); return mat3(1.,0.,0.,0.,c,-s,0.,s,c); }
mat3 rotY(float a) { float s = sin(a), c = cos(a); return mat3(c,0.,s,0.,1.,0.,-s,0.,c); }
mat3 rotZ(float a) { float s = sin(a), c = cos(a); return mat3(c,-s,0.,s,c,0.,0.,0.,1.); }

// Identity matrix.
mat3 identity() {  return mat3( vec3(1, 0, 0), vec3(0, 1, 0), vec3(0, 0, 1) ); }

float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
  p = abs(p);
  vec2 b = vec2(la,lb);
  float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
  vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
  return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}

vec3 HacerPelo(vec3 p0, vec3 res , mat3 m1)
{   vec3 p=p0*m1;
    float dpelo1=sdCylinderXZ(p-vec3(0.0, 0.0,0.0), vec2(0.8,1.2) );
    float dpelo2=sdCylinderXZ(p-vec3(0.0, -1.0,0.0),vec2(0.3,2.7) );   
    res =opU3(res, vec3(dpelo1,1.0,-1.0));
    res =opU3(res, vec3(dpelo2,2.0,-1.0));
    return res;
}


float OndaDoble(vec2 uv,float  t)
{   float d1 =uv.y+ sin(uv.x*5.+t)*0.2;
    float d2 =uv.y+ sin(uv.x*5.+t)*0.2+0.2;   
    float dfin= differenceSDF(d1, d2);
    return dfin; }

vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0); 
    p.y=p.y-10.0;
    vec3 p0=p;
    mat3 m1=rotZ(radians(-20.0));
    mat3 m2=rotZ(radians(20.0));
    mat3 m3=identity();
    res= HacerPelo(p-vec3(-3.5,8.0,-2.0),res,m1 );
    res= HacerPelo(p-vec3(-1.0,9.0,-2.0),res,m3 );
    res= HacerPelo(p-vec3( 1.0,9.0,-2.0),res,m3 );
    res= HacerPelo(p-vec3( 3.5,8.0,-2.0),res,m2 );

    float dCabeza= sdEllipsoid(p-vec3(0.0,0.0,-2.0), vec3(5.0,6.0,1.0) );

    float la=2.7; float lb=2.0; float h=3.0; float ra=2.0;
    float dbarbilla= sdRhombus(p.xzy-vec3(0.0,-1.0,-2.0), la,lb, h, ra);

    float dFrente= sdBox(p-vec3(0.0,2.0,-1.0), vec3(4.0,2.5,0.2));
    dCabeza= opSmoothUnion(dFrente, dCabeza, 1.85 );

    float dbarBxdif= sdBox(p-vec3(0.0,0.5,0.0), vec3(5.0,2.5,4.5));
    float dCabezaBxdif= sdBox(p-vec3(0.0,-4.5,0.0), vec3(5.2,2.5,4.5));

    //ojo
    p.x=abs(p.x)-2.5;
    vec3 p2=p0;
    p2.x=abs(p2.x)-2.2;
    float dOjo=sdEllipsoid(p-vec3(0.0,0.0,-2.8), vec3(1.5,1.3,1.0) );
    float dOjoHueco=sdEllipsoid(p-vec3(0.0,0.0,-3.0), vec3(1.5,1.3,1.0) );
    float dPupilaAnillo= sdCylinderXY( p2-vec3(0.0,0.5,-1.0), vec2(0.76,3.0)  );
    float dPupila= sdCylinderXY( p2-vec3(0.0,0.5,-1.0), vec2(0.7,3.0)  );
    float dIris= sdCylinderXY( p2-vec3(0.0,0.5,-1.5), vec2(0.45,3.0)  );


    float dOjoBxdif= sdBox(p-vec3(0.0,-1.1,-1.0), vec3(1.7,1.0,3.2));
    dOjoHueco=differenceSDF(dOjoHueco,dOjoBxdif); 
    dOjo     =differenceSDF(dOjo,dOjoBxdif); 
    dOjo=intersectSDF(dCabeza,dOjo);
    //interseccion a ojo
    dPupilaAnillo=intersectSDF(dOjo,dPupilaAnillo);
    dPupila=intersectSDF(dOjo,dPupila);
    dIris=intersectSDF(dOjo,dIris);


    dbarbilla=differenceSDF(dbarbilla,dbarBxdif);

    dbarbilla=intersectSDF(dCabeza,dbarbilla); //diferencia barbilla cabeza
    dCabeza=differenceSDF(dCabeza,dCabezaBxdif);  //diiferencia infeior
    dCabeza=unionSDF(dCabeza,dbarbilla);
    dCabeza=opSmoothSubtraction(dOjoHueco,dCabeza,0.25); 

    ///nariz
    p=p0;
    p=rotate_x(p-vec3(0.0,-2.0,-2.9), radians(-5.0));
    float dNarizCen= sdCapsule( p, vec3(0.0,0.0,0.0), vec3(0.0,2.0,0.25), 0.35 );
    p=p0;
    float dNarizIzq= sdCapsule( p-vec3(0.0,-2.0,-2.8), vec3(-0.5,0.0,0.0), vec3(0.0,1.0,0.25), 0.25 );
    float dNarizDer= sdCapsule( p-vec3(0.0,-2.0,-2.8), vec3( 0.5,0.0,0.0), vec3(0.0,1.0,0.25), 0.25 );
    float dNarizCirIzq= sdSphere( p-vec3(-0.5,-1.8,-3.0), 0.15 );
    float dNarizCirDer= sdSphere( p-vec3( 0.5,-1.8,-3.0), 0.15 );

    dCabeza=opSmoothUnion(dCabeza,dNarizCen,0.5);
    dCabeza=opSmoothUnion(dCabeza,dNarizIzq,0.5);
    dCabeza=opSmoothUnion(dCabeza,dNarizDer,0.5);
    //boca
    float donda= OndaDoble(p.xy*0.5-vec2(0.0,-1.5),iTime*2.5);
    float dboca= sdBox(p-vec3(0.0,-3.5,-2.0), vec3(3.0,1.0,2.5));
    dboca=intersectSDF(dboca,donda); 
    dboca=intersectSDF(dboca,dCabeza); 

    res =opU3(res, vec3(dCabeza,33.0,-1.0));
    res =opU3(res, vec3(dOjo,1.0,-1.0));
    res =opU3(res, vec3(dPupilaAnillo,0.0,-1.0));
    res =opU3(res, vec3(dPupila,3.0,-1.0));
    res =opU3(res, vec3(dIris,0.0,-1.0));
    res =opU3(res, vec3(dboca,2.0,-1.0));
    return res;
}

