vec2 Cell( in ivec2 p )
{
    // do wrapping
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;

    // fetch texel
    return texelFetch(iChannel0, p, 0 ).xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    ivec2 px = ivec2(fragCoord);

    float a = atan(uv.y, uv.x);
    float r = length(uv);

    vec2 p = Cell(px);
    if (iFrame == 0 || key_down(KEY_RESET)) {
        fragColor = vec4(0.);
        p = vec2(0.5,0.5);
        //return;
    } 

    vec2 ipos = floor(sc * uv);

    float h = h21(uv);
    float m = 2. / iResolution.y;//h - 0.5 + 0.5 * cos(iTime);
    float th = iTime + 28. * r + 0.5 * pi * cos(1. * a + iTime);//0.1 * pi * thc(1., 4. * r + iTime);//2. * pi * h21(vec2(0.01 * iTime)); // not perfect
    vec2 b = vec2(thc(4., th), ths(4., th));

    vec2 p2 = p + m * b;
    //p2 = clamp(p2, vec2(0.), vec2(1.));
    fragColor = vec4(p2.xy, 0., 1.);
}

#define pi 3.14159
#define sc 10.


float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

