void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, I,F;
 // R/=2.; U/=2.; 
    if ( U.y > R.y/2. )        U  = roundEven(U);
    if ( abs(U/R-.5).y < .25 ) U += .5;
    O = U.x < R.x/2.         ? texelFetch(iChannel0, ivec2(U)%256, 0)
                             : texture(iChannel0,U/256.);
    if ( abs(U/R-.5).x < .25 ) O = fwidth(O);
    O = sqrt(.75*O);
}
