//ollj quaternionRotation math
//
//ANY rotations in 3d are non-commutative!
//
//matrix rotations are just bulky, memory wasting
//EulerRotations almost certainly fail to rotate over the SHORTEST path.
//EulerRotations almost certainly will gimbalLock and get stuck along one axis
//QuaternionRotations are superior here.
//-> we only use EulerRorations for simple input devices (keyboard input)
//-> we convert to quaternions, buffered as vec4.

//quaternion Identity
vec4 qid() 
{
    return vec4(0, 0, 0, 1);
}

//return quaternion from axis and angle
vec4 aa2q(vec3 axis, float ang) 
{
    vec2 g = vec2(sin(ang), cos(ang)) * 0.5;
    return normalize(vec4(axis * g.x, g.y));
}

//return AxisAngle of NORMALIZED quaternion input
vec4 q2aa(vec4 q) 
{
    return vec4(q.xyz / sqrt(1.0 - q.w * q.w), acos(q.w) * 2.);
}

//return q2, rotated by q1, order matters (is non commutative) : (aka quaternion multiplication == AxisAngleRotation)
vec4 qq2q(vec4 q1, vec4 q2) 
{
    return vec4(q1.xyz * q2.w + q2.xyz * q1.w + cross(q1.xyz, q2.xyz), (q1.w * q2.w) - dot(q1.xyz, q2.xyz));
}

//extension to qq2q(), scaled by sensitivity [f] (==quaternion ANGULAR equivalent to slerp() )
vec4 qq2qLerp(vec4 a, vec4 b, float f) 
{
    float d = dot(a, b), t = acos(abs(d)), o = (1. / sin(t));
    return normalize(a * sin(t * (1.0 - f)) * o * sign(d) + b * sin(t * f) * o);
}

//doing qq2q() multiple times, you need to normalize() the quaternion, to fix rounding errors.
//how often you do this is up to you.

//normalize q (assuming length(q) is already close to 1, we can skip whe sqrt()
vec4 qn(vec4 q) 
{
    return q / dot(q,q);
}

//return quaternion, that is the shortest rotation, between looking to [a before], and looking to [b after] the rotation.
//http://wiki.secondlife.com/wiki/LlRotBetween
vec4 qBetween(vec3 a, vec3 b) 
{
    float v = sqrt(dot(a,a) * dot(a,a));

    if(v == 0.) return qid();

    v = dot(a, b) / v;
    vec3 c = a.yzx * b.zxy - a.zxy * b.yzx / v;
    float d = dot(c,c);

    if(d != 0.) 
    {
        float s = (v > - 0.707107) ? 1. + v : d / (1. + sqrt(1. - d));
        return vec4(c, s) / sqrt(d + s * s);
    }

    if(v > 0.) return qid();

    float m = length(a.xy);

    return (m != 0.) ? vec4(a.y, - a.x, 0, 0) / m : vec4(1, 0, 0, 0);
}

//return inverse of quaternion
vec4 qinv(vec4 q) 
{
    return vec4(- q.xyz, q.w) / dot(q,q);
}

//return VECTOR p, rotated by quaterion q;
vec3 qv2v(vec4 q, vec3 p) 
{
    return qq2q(q, qq2q(vec4(p, .0), qinv(q))).xyz;
}

//qv2v()  with swapped inputs
//return quaterion P (as vector), as if it is rotated by VECTOR p (as if it is a quaternion)
vec3 vq2v(vec3 p, vec4 q) 
{
    return qq2q(qinv(q), qq2q(vec4(p, 0.0), q)).xyz;
}

vec3 vq2v(vec4 a, vec3 b) 
{
    return qv2v(a, b);
}

//in case of namespace confuction
vec3 qv2v(vec3 a, vec4 b) 
{
    return vq2v(a, b);
}

//return mat3 of quaternion (rotation matrix without translation)
//https://www.shadertoy.com/view/WsGfWm
mat3 q2m(vec4 q) 
{
    vec3 a = vec3(-1, 1, 1);
    vec3 u = q.zyz * a, v = q.xyx * a.xxy;
    mat3 m = mat3(0.5) + mat3(0, u.x,u.y,u.z, 0, v.x,v.y,v.z, 0) * q.w + matrixCompMult(outerProduct(q.xyz, q.xyz), 1. - mat3(1));
    q *= q; 
    m -= mat3(q.y + q.z, 0, 0, 0, q.x + q.z, 0, 0, 0, q.x + q.y);
    return m * 2.0;
}

//return quaternion of orthogonal matrix (with determinant==1., or else quaternionm will not be normalized)
vec4 m2q(mat3 m) 
{
#define m2f(a,b) m[a][b]-m[b][a]
    //http://www.euclideanspace.com/maths/geometry/rotations/conversions/matrixToQuaternion/
    float q = 2. * sqrt(abs(1. + m[0][0] + m[1][1] + m[2][2]));
    return vec4(vec3(m2f(2, 1), m2f(0, 1), m2f(1, 0)) / q / 4., q);
#undef m2f
}

float at2e(vec2 a) 
{
    a *= 2.;
    return atan(a.x, 1. - a.y);
}

//return quaternion of Euler[yaw,pitch,roll]     
vec4 eYPR2q(vec3 o) 
{
    o *= .5;
    vec3 s = sin(o);
    //https://en.wikipedia.org/wiki/Conversion_between_quaternions_and_Euler_angles#Source_code
    o = cos(o);
    vec4 a = vec4(s.xz, o.xz);
    return a.yyww * a.zxxz * o.y + a.wwyy * a.xzzx * s.y * vec4(-1, 1, -1, 1);
}

vec4 eYPR2q(vec2 o) 
{
    o *= .5;
    vec2 s = sin(o);
    o = cos(o);
    vec4 a = vec4(s.x, 0., o.x, 0.);
    return a.yyww * a.zxxz * o.y + a.wwyy * a.xzzx * s.y * vec4(- 1, 1, - 1, 1);
}

mat3 getCam(vec4 q) 
{
    return q2m(q);
}



// Rayleigh extinction coefficients, 
// calculated for a nitrogen gas (polarizability 1.82e-30)
// with temp 290 K, press 101300 Pa, wavelengths 655 (R), 540 (G) and 425 (B) nm,
// and a scale of 1 unit = 6400 km (earth radius).
vec3 beta =vec3( 38.05, 82.36, 214.65 );	

// a small absobtion in the orange band is added
// to simulate the effect of ozone
vec3 absorb = vec3( 0.75, 0.85, 1. );			

// devrived values
float LN2 = 0.693147181;
float H50;
vec3 beta50;
float invH50;
float X50;

void init()
{
   H50 = H * LN2;
   beta50 = beta * LN2;
   invH50 = 1. / H50;
   X50 = R / H50;
}
// --------------------------------------------------------

// sphere of size ra centered at point ce
vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(1e10); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

// --------------------------------------------------------

float chapman( float X, float h, float coschi )
{
	// this is the approximate Chapman function,
	// corrected for transitive consistency 

	float c = sqrt( X + h );    

	if( coschi >= 0. )
	{	
 		return c / ( c * coschi + 1. ) * exp2( -h );
	}
	else
	{
		float x0 = sqrt( 1. - coschi * coschi ) * ( X + h );
		float c0 = sqrt( x0 );    
		return 2. * c0 * exp2( X - x0 ) - c / ( 1. - c * coschi ) * exp2( -h );
	}
}

// --------------------------------------------------------

vec3 transmittance( vec3 r, vec3 viewdir )
{
	// a quick function to get the transmittance
	// looking from point r into infinity

	float rsq = dot(r,r);
	float invrl = inversesqrt( rsq );
	float len = rsq * invrl;
	float x = len * invH50;
	float h = x - X50;
	float coschi = dot( r, viewdir ) * invrl;

	return beta50 * H50 * chapman( X50, h, coschi ) ;
}

float density(vec3 r)
{
	float rsq = dot(r,r);
	float invrl = inversesqrt( rsq );
	float len = rsq * invrl;
	float x = len * invH50;
	float h = x - X50;
    return exp2(-h);
}

float HenyeyGreenstein(float g, float costh)
{
    return (1.0 - g * g) / (4.0 * PI * pow(1.0 + g * g - 2.0 * g * costh, 3.0/2.0));
}

float scatterAnisotropy(vec3 ri, vec3 ro)
{
    return mix(1.0, 4.*PI*HenyeyGreenstein(0.5, dot(ri, ro)), 0.33) + HenyeyGreenstein(0.9995, dot(ri, ro));
}

vec3 scatter(vec3 r, vec3 rd, vec3 ld)
{
    return beta50*scatterAnisotropy(rd, ld)*density(r);
}

vec4 atmo(vec3 r, vec3 rd, float td)
{
    float inv = sign(dot(rd,normalize(r)));
    float hit = 1.0;

    vec3 depth = inv*(transmittance(r, rd*inv) - transmittance(r + rd*td, rd*inv));

    return vec4(exp2(-max(depth, 0.0)), hit);
}

const float samp = 16.0;

//atmosphere illuminated by a sun
vec3 atmo2(vec3 r, vec3 rd, float d, out vec3 vis, out vec3 inc)
{
    vec2 si0 = sphIntersect(r, rd, vec3(0), R + H*10.0);

    //range between the start of the atmosphere and end/surface
    vec2 range = vec2(max(si0.x,0.0), min(si0.y, d));
    float dr = range.y - range.x;
    vec3 L = vec3(0.0);
    float dx = 1.0/samp;
    for(float t = 0.0; t <= 1.0; t+=dx)
    {
        float td = mix(range.x, range.y, t);
        vec3 p = r + rd*td;
        vec3 shadow = exp2(-transmittance(p, light));
        vec4 cam = atmo(r, rd, td);
        inc = shadow;
        vec3 scattering = scatter(p, rd, light)*inc;
        vis = cam.xyz;
        L += vis*scattering*dx;
    }

    return light_bright*L*dr;
}
