vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  
    vec3 p0=p;
    p.y-=8.0;

    float dCuello=sdCylinderXZ( p-vec3(0.0,10.0,1.0), vec2(2.0,2.0) );

    float dCuerpo= sdRoundBox(p-vec3(0.0,0.0,1.5), vec3(7.0,7.2,2.0), 0.5 );
    p.x=abs(p.x)-8.0;
    float dCurva= sdEllipsoid( p-vec3(0.0,0.0,0.0), vec3(3.2,10.0,12.0) );

    dCuerpo= opSmoothUnion(dCuerpo, dCuello,0.5);
    dCuerpo= differenceSDF(dCuerpo, dCurva);

    p=p0;
    p.x=abs(p.x)-3.0;
    float dPecho1=sdSphere( p-vec3(0.0,10.5,-0.5),2.5 );
    float dPechoBx=sdBox( p-vec3(0.0,13.0,-0.5), vec3(3.0,2.0,5.0) );
    float dSosten=sdBox( p0-vec3(0.0,10.5,-0.4), vec3(1.0,0.5,1.0) );
    dPecho1= differenceSDF(dPecho1, dPechoBx);
    float dPecho2=sdSphere( p-vec3(0.0,10.5,-0.3),2.5 );


    dCuerpo= opSmoothUnion( dCuerpo, dPecho1, 0.5 ) ;
    dPecho1= opSmoothUnion( dPecho1, dSosten, 0.5 ) ;

    p=p0;
    float dHombro= sdVerticalCapsule( p.zxy-vec3(1.5,-7.8,16.0), 15.0, 2.0 );
    //----Brazo Izq
    float ang1A=radians(120.0+ 60.0*clamp(-0.50,0.0, cos(0.5+iTime*1.0)) );
    vec3 p1A= rotate_x(p-vec3(-8.0,16.0,1.5), ang1A);
        float ang2A=radians( 120.0*clamp(-0.50,0.0, sin (iTime*1.0)) );
    vec3 p2A= rotate_x(p1A-vec3(0.0,8.5,0.0), ang2A);
    float dbrazoA1=sdRoundCone(p1A, 1.4,0.8, 7.2 );
    float dbrazoA2=sdRoundCone(p2A, 1.0,0.6, 7.0 );



    //brazo der
    p=p0;
       float ang1B=radians(120.0+ 60.0*clamp(-0.50,0.0, cos(0.5+iTime*1.0)) );
       float ang1B1=radians(60.+ 60.0*clamp(-0.50,0.0, cos(0.5+iTime*1.0)) );
    vec3 p1B= rotate_x(p-vec3(8.0,16.0,2.5), ang1B);
    p1B= rotate_z(p1B, ang1B1);
        float ang2B=radians( 120.0*clamp(-0.50,0.0, sin (iTime*1.0)) );
    vec3 p2B= rotate_x(p1B-vec3(0.0,8.5,0.0), ang2B);

    float dbrazoB1=sdRoundCone(p1B, 1.4,0.8, 7.2 );
    float dbrazoB2=sdRoundCone(p2B, 1.0,0.6, 7.0 );

    dCuerpo= opSmoothUnion(dCuerpo, dHombro,0.5);
     dCuerpo= opSmoothUnion(dCuerpo, dbrazoA1,0.5);
     dCuerpo= opSmoothUnion(dCuerpo, dbrazoA2,0.5);
     dCuerpo= opSmoothUnion(dCuerpo, dbrazoB1,0.5);
     dCuerpo= opSmoothUnion(dCuerpo, dbrazoB2,0.5);

    res =opU3(res, vec3(dCuerpo,30.0,-1.0));
    res =opU3(res, vec3(dPecho2,30.0,-1.0));
    res =opU3(res, vec3(dPecho1,1.0,-1.0));

    res =opU3(res, vec3(dbrazoA1,30.0,-1.0));
    res =opU3(res, vec3(dbrazoA2,30.0,-1.0));
    res =opU3(res, vec3(dbrazoB1,30.0,-1.0));
    res =opU3(res, vec3(dbrazoB2,30.0,-1.0));


    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

float GetShadow(vec3 p, vec3 plig)
{   vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

vec3 amb(vec3 c, float k)
{  return c * k; }

float diff(vec3 p,vec3 lp,vec3 n )
{   vec3 l = normalize(lp - p);
    float dif = clamp(dot(l, n), 0.0, 1.0);
    return dif;
}
float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{ vec3 l = normalize(lp - p);
  vec3 r = reflect(-l, n);
  float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 20.0);
  return spe;
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col) 
{   vec3 l = normalize(lp - p);
    vec3 ldir = normalize(p-rd);    
    float distA = max(length(l), 0.01);
    float distB = 1.0/(length(p-lp));
    float dist=(distA+distB)/2.0;
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= (dist);

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    vec3 amb=amb(col, 0.5);
    float dif = diff( p, lp, n );

    float diff=max(dot(normalize(p-lp), -n), 0.0);;
    vec3 ln=normalize(lp);

    float spe= spec(  p,  lp, rd, n );
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);
     float bac=clamp(dot(n,-l),0.0,1.0);
    float rim=pow(1.0+dot(n,rd),3.0);
    float dn=.15*max(0.,dot(normalize(rd),-n));

     vec3  hal = normalize(-rd+l);
     float dif3 = clamp( dot(n,l), 0.0, 1.0 );
     float amb2 = clamp( 0.5 + 0.5*dot(n,vec3(0.0,1.0,0.0)), 0.0, 1.0 );
     float occ = 0.5 + 0.5*n.y;

    float fshadow;
    float sh = clamp(dot(n,normalize(lp)),0.0,1.0);

    if (mObj.blnShadow==true) {fshadow=GetShadow(p,lp);}
    else {fshadow=0.5;}

    vec3 lin=vec3(1.0);
    lin*= amb*amb2*occ;
    lin += 1.0*(dif+diff+dif3)*sh;
    lin += 2.5*spe*vec3(1.0);
    lin += 2.5*fre*vec3(1.);
    lin *= atten*0.5*col*fshadow;
    lin *= vec3(1.0)*  max(normalize(vec3(length(lin))).z, 0.)+ .75; 
    lin = pow(lin,vec3(0.4545));

    return lin;
    //return lin*atten*(1.0/2.0);

}

