#define R iResolution.xy
#define T1(U) texture(iChannel0,U/R)

void mainImage( out vec4 C, in vec2 U )
{   

    float m = 2.*T1(U).w;//masse
    float v = length(T1(U).xy)/10.;//velocity
    float p = T1(U).z/10.;//pressure

    C = m*vec4(1,1.2,2,1)+log(1.+.5*v);

    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) C = vec4(0.5);
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) C = vec4(0.5);

}

void mainImage( out vec4 Q, in vec2 U )
{      
    R = iResolution.xy; t = iTime; Mouse = iMouse; Frame = iFrame;
    pass( Q, iChannel0, U );

}

#define dt .1
#define g 4.
#define k 7.
#define l 0.
#define s .12
#define nu 0.1
#define mu 0.1
#define kappa .0
#define h 6.0

vec2 R;
vec4 Mouse;
float t;
int Frame;

#define T(p) texture(ch,(p)/R)

void pass( out vec4 Q, in sampler2D ch, in vec2 U )
{   

    vec4 a = T(U+vec2(1,0)),b = T(U+vec2(0,1)),c = T(U+vec2(-1,0)),d = T(U+vec2(0,-1)),e=.25*(a+b+c+d), dx=a-c, dy=b-d;



    vec2 vu = vec2(0.);//advection vector estimate
    float n = 0.;
    for(float i=-h;i<=h;i++)for(float j=-h;j<=h;j++){
        vec2 vv = U+vec2(i,j)*.25;
        vec2 dp = abs(vv+dt*T(vv).xy-U);
        if(dp.x<.25&&dp.y<.25)vu+=T(vv).xy,n++;
    }
    //if(n>1.)vu=T(U).xy;//?
    Q = T(U-dt*vu);//advection
 	vec2 gp = vec2(dx.z,dy.z);//pressure gradient
    vec2 gw = vec2(dx.w,dy.w);//density gradient
    float div = (dx.x+dy.y);//divergence
    vec2 vdv = vec2(Q.x*dx.x+Q.y*dy.x,Q.x*dx.y+Q.y*dy.y); 


    Q.xy -= dt *(k*gp + Q.w*l*gw + Q.w*vec2(0.,g)+ s*vdv*Q.w);//forces
    Q.z = e.z - dt*0.29*(div*Q.z+dot(gw,Q.xy));//pressure
    Q -= dt * vec4(nu,nu,mu,kappa) * 4.*(Q-e);//diffusion

    //Boundaries
    if (Frame < 1) Q = vec4(0,0,0.5,0);
    if (U.x < 1.||U.y < 1.) Q.xy *= 0.;
    if (R.x-U.x < 10.) Q.w *= (R.x-U.x)*.1, Q.z*=(R.x-U.x)*.1;
    if (R.y-U.y < 10.) Q.w *= (R.y-U.y)*.1, Q.z*=(R.y-U.y)*.1;
    if (U.x < 0.2*R.x&&U.y < 0.5*R.y) Q.xy *= 0.;
    if (U.x < 0.4*R.x&&U.y < 0.3*R.y) Q.xy *= 0.;
 	if (length(U-vec2(0,0.9*R.y)) < 8.) {Q.xy= Q.xy*.5+.5*vec2(1.+0.4*sin(1.5*t),0); Q.w = 1.;};

    //click mouse to add a circular obstacle
    if(length(Mouse.xy - U) < 20. && Mouse.z > 0.5){
        Q.xyw = vec3(0.);
    }


    Q = clamp(Q, vec4(-15,-15,0,0), vec4(15,15,8,15));
}

void mainImage( out vec4 Q, in vec2 U )
{      
    R = iResolution.xy; t = iTime; Mouse = iMouse; Frame = iFrame;
    pass( Q, iChannel0, U );

}
