// presentation

void paint() {
    vec4 ui_state = texelFetch(iChannel0, ivec2(0), 0);
    vec2 uv = get_origin();

    vec2 m = get_query();

    vec2 p = fetch(iChannel1, uv).xy;

#if USE_MAXNORM
    float d = Linf_length(uv - p);
#else
    float d = length(uv - p);
#endif

    vec3 col = vec3(p*0.5+0.5, 0.0);
    {
        col = vec3(sin((d*5.0-iTime*0.3)*radians(360.))*0.5+0.5);
        if (map(uv) < 0.0) {
            col = (1.0 - col)*0.5;
        } else {
            col = 0.3 * col + 0.7;
        }
    }

    set_source_rgb(col);
    clear();

    set_line_width_px(2.0);

    set_source_rgb(vec3(0.5,0,1));
#if VIZ_QUADTREE
    vec4 r = vec4(-1, -1, 1, 1);
    rectangle(r.xy, r.zw - r.xy);
    stroke();

    for (int i = 0; i < 6; ++i) {
        vec2 c = (r.xy + r.zw) / 2.0;
        vec2 p = fetch(iChannel1, c).xy;
        #if USE_MAXNORM
        float d = Linf_length(c - p);
        float w = Linf_length(c - r.xy);
        #else
        float d = length(c - p);
        float w = length(c - r.xy);
        #endif
        if (w <= d)
            break;    
        if (uv.x >= c.x)
            r.x = c.x;
        else
            r.z = c.x;
        if (uv.y >= c.y)
            r.y = c.y;
        else
            r.w = c.y;
        rectangle(r.xy, r.zw - r.xy);
        stroke();        
    }    
#endif
    vec2 s = fetch(iChannel1, m).xy;
#if USE_MAXNORM
    float rd = Linf_length(s - m);
    rectangle(m - rd, vec2(rd) * 2.0);
#else
    float rd = length(s - m);
    circle(m, rd);
#endif
    stroke();

    if (ui_state.w > 0.5) {
        // raymarch
        vec2 ro = ui_state.xy;
#if USE_MAXNORM
        float max_t = Linf_length(m - ro);
        vec2 rd = Linf_normalize(m - ro);
#else        
        float max_t = length(m - ro);
        vec2 rd = normalize(m - ro);
#endif

        move_to(ro);
        line_to(ro + rd*max_t);
        stroke();

        set_source_rgb(vec3(1,0.,0.5));
        float t = 0.0;
        float th = 0.0;
        float sg = sign(map(ro));
        for (int i = 0; i < 20; ++i) {
            if (t > max_t)
                break;
            vec2 q = ro + rd*t;
#if USE_MAXNORM
            vec2 d = vec2(map(q), Linf_length(fetch(iChannel1, q).xy - q));
            rectangle(q - abs(d.y), 2.0 * vec2(abs(d.y)));
#else
            vec2 d = vec2(map(q), length(fetch(iChannel1, q).xy - q));
            circle(q, abs(d.y));
#endif
            stroke();
            if (d.x*sg < 1e-3) {
                break;
            }
            th = t + d.x;
            t += d.y;
        }
        set_source_rgb(vec3(1,0.7,0));
        if (t < max_t) {
            // bisect
            vec2 iv = vec2(th, t);
            for (int i = 0; i < 10; ++i) {
                t = (iv[0] + iv[1])/2.0;
                vec2 q = ro + rd*t;
                circle_px(q, 3.0);
                fill();
                float d = map(q)*sg;
                if ((abs(d) < 1e-3)||(i == 9)) {
                    set_source_radial_gradient(vec4(0,1,0,1),vec4(0,0,0,0),q,AAINV*50.0);
                    rings(q, AAINV*7.0, 0.0);
                    stroke();
                    break;
                }
                iv[(d < 0.)?1:0] = t + d;
            }
        }


    }   

}

