// channel 1: JFA steps

const vec4 NULL_PIXEL = vec4(-1000.0);
bool is_null(vec4 p) {
    return p.w < -500.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5) / iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x / iResolution.y;
    int iter = iFrame % 13;
    if (iter == 0) {
        // init
        float d = map(uv);
        if (abs(d) <= (sqrt(2.0)/(min(iResolution.x,iResolution.y)))) {
            fragColor = vec4(uv,uv);
        } else {
            fragColor = NULL_PIXEL;
        }
    } else {
        // JFA step (for up to 4096x4096)
        int level = clamp(iter-1,0,11);
        int stepwidth = 1<<(11 - level);

        ivec2 tc = ivec2(fragCoord);
        ivec2 res = ivec2(iChannelResolution[0].xy);

        float best_dist = 999999.0;
        vec4 best_coord = NULL_PIXEL;
        for (int y = -1; y <= 1; ++y) {
            for (int x = -1; x <= 1; ++x) {
                ivec2 fc = tc + ivec2(x,y)*stepwidth;
		        vec4 ntc = texelFetch(iChannel0, (fc + 16 * res) % res, 0);
                if (is_null(ntc))
                    continue;
                vec2 dv = ntc.xy - uv;
                vec2 g = grad(ntc.xy);
                float qsd = map(uv);
                #if USE_SDF
                const float a = 1.0;
                #else
                float a = dot(normalize(dv), g)*sign(qsd);
                #endif
                #if USE_MAXNORM
                float d = Linf_length(dv);
                #else
                float d = length(dv);
                #endif
                if ((d <= 0.0) || ((a > max_rdf_angle_cos) && (d < best_dist))) {
                    best_dist = d;
                    best_coord = ntc;
                }
            }
        }        
        fragColor = best_coord;
    }
}

// channel 2: take snapshot of stage

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 tc = ivec2(fragCoord);
    int iter = int(mod(float(iFrame),13.0));
    int frame = int(mod(iTime,15.0));
    if (iter == 12) {
        // snapshot    
        fragColor = fetch(iChannel0, ivec2(iChannelResolution[0].xy), tc);
    } else {
        // copy
        fragColor = fetch(iChannel1, ivec2(iChannelResolution[1].xy), tc);
    }
}

/*
by Leonard Ritter, Duangle GbR

This shadertoy uses the jump flooding algorithm[1] to generate a 
Relaxed Distance Field from a signed distance field, inspired by
relaxed cone stepping[2]. 

The RDF can be used to raymarch through a signed distance field 
in fewer steps, guaranteeing that we never skip more than one
surface. We step until the signed distance is negative, indicating
that we have landed inside the surface. The root can then be
searched either using bisection of the interval between our latest
position and the current one, by regular sphere marching,
or by a combination of the two.

A RDF can also be used to partition a volume into polarconvex
cells, so that the content of each cell can be trivially represented
by a height/depth map embeddable on the surface of a convex 
polyhedron, such as a tetrahedron, octahedron, cube or any other
spherical approximation.

The relaxed distance is defined as the distance to the nearest
backfacing surface point. A surface point is backfacing when
the dot product of its local gradient and the distance vector
from query to surface point is greater than zero. When the
query point is inside a volume (that is, its SDF is negative),
then the dot product must be negated before querying.

Although no errors are known to the author, the RDF generated by 
this shader might possibly be spotty in places. It is to the author's
knowledge though the first attempt at an algorithm that performs 
better than O(n²), or, in the 3d case, O(n³).

[1] https://en.wikipedia.org/wiki/Jump_flooding_algorithm
[2] https://developer.nvidia.com/gpugems/gpugems3/part-iii-rendering/chapter-18-relaxed-cone-stepping-relief-mapping
*/

// if 1, use maxnorm rather than euclidean distance
#define USE_MAXNORM 1
// if 1, use strict signed distance field rather than relaxed distance field
#define USE_SDF 0
// if 1 visualize a quadtree over the domain
#define VIZ_QUADTREE 0

// maximum angle in degrees between view ray and surface normal permitted to be contained in cell
const float max_rdf_angle = 90.0;
const float max_rdf_angle_cos = -cos(radians(min(90.0, max_rdf_angle)));

float Linf_length(vec2 v) {
    v = abs(v);
    return max(v.x, v.y);
}

vec2 Linf_normalize(vec2 v) {
    float L = Linf_length(v);
    return (L == 0.0)?v:(v / L);
}

// from https://www.shadertoy.com/view/XdXBRH
vec2 hash( in vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}


// return gradient noise (in x) and its derivatives (in yz)
vec3 noised( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

#if 1
    // quintic interpolation
    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.0*f*f*(f*(f-2.0)+1.0);
#else
    // cubic interpolation
    vec2 u = f*f*(3.0-2.0*f);
    vec2 du = 6.0*f*(1.0-f);
#endif    

    vec2 ga = hash( i + vec2(0.0,0.0) );
    vec2 gb = hash( i + vec2(1.0,0.0) );
    vec2 gc = hash( i + vec2(0.0,1.0) );
    vec2 gd = hash( i + vec2(1.0,1.0) );

    float va = dot( ga, f - vec2(0.0,0.0) );
    float vb = dot( gb, f - vec2(1.0,0.0) );
    float vc = dot( gc, f - vec2(0.0,1.0) );
    float vd = dot( gd, f - vec2(1.0,1.0) );

    return vec3( va + u.x*(vb-va) + u.y*(vc-va) + u.x*u.y*(va-vb-vc+vd),   // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.x*u.y*(ga-gb-gc+gd) +  // derivatives
                 du * (u.yx*(va-vb-vc+vd) + vec2(vb,vc) - va));
}


float rect(vec2 p, vec2 r) {
    vec2 q = abs(p) - r;
    return max(q.x, q.y);
}

float map(vec2 p) {
#if 1
    return (noised(p*4.0).x + noised(p).x + 0.2 /*+ noised(p*20.0).x * 0.2*/) * 0.25;
#elif 1
    float c1 = length(p + 0.2) - 0.3;
    float c2 = length(p - 0.2) - 0.2;
    float c4 = length(p + 0.3) - 0.2;

    float c3 = rect(p - vec2(0.4,-0.1), vec2(0.1,0.3));

    return min(max(min(c1,c2),-c4),c3);
#else
    float d5 = rect(p, vec2(0.3,0.1));
    return d5;
#endif
}

vec2 grad(vec2 p) {
    vec2 eps = vec2(1e-4, 0.0);
    return normalize(vec2(    
        map(p + eps.xy) - map(p - eps.xy),
        map(p + eps.yx) - map(p - eps.yx)
    ));
}

vec4 fetch(sampler2D sampler, ivec2 res, ivec2 p) {
    return texelFetch(sampler, p, 0);
}

