// if defined, shows how the spherical L1 arclength mapping is tilable in 2D
//#define SHOW_TILING

// if defined, shows latitude/longitude lines
#define SHOW_FLOW_LINES

// if defined, shows an environment map instead of a XYZ color gradient
// for inverse mode, demonstrates texture mapping
//#define SHOW_CUBEMAP

// if defined, shows the latitude as cos(theta) instead of theta
//#define SHOW_COS_THETA

// if defined, shows a UV tesselation of quadliterals, and the proportional
// difference between the quadliteral area and the expected average size (pi/N²)
// if FIX_EQUAL_AREA is defined, the proportions will be ideal.
//#define SHOW_SOLID_ANGLE

// if defined, adjusts the latitude so that equal spaced points on the map
// are equal spaced on the sphere; if not defined, the latitude will map
// exactly to the L1 radius.
#define FIX_EQUAL_AREA

// if not defined, computes a more accurate solid angle from cosines,
// though the precision of this method is terrible and is only relevant
// for rough tesselations anyway.
#define USE_TRIANGLE_AREA

// if defined, demonstrate the inverse mapping 
//#define SHOW_INVERSE
#define SHOW_GUYOU_MAP

// subdivisions per quadrant
#if defined(SHOW_SOLID_ANGLE)||defined(SHOW_INVERSE)
#undef SHOW_FLOW_LINES
// solid angle subdivisions
const float subdivisions = 16.0;
#else
// flow line subdivisions
const float subdivisions = 8.0;
#endif

// thesis related functions
//////////////////////////////////////////////////////////

vec2 oct_wrap (vec2 v) {
    return (1.0 - abs(v.yx)) * sign(v);
}

// https://jcgt.org/published/0003/02/01/
// uv is [-1..1]x[-1..1]
vec3 octahedral_surface(vec2 uv) {
    float z = 1.0 - abs(uv.x) - abs(uv.y);
    return vec3((z >= 0.0)?uv:oct_wrap(uv), z);
}

// spherical coordinates from points on L1 surface
vec2 L1_to_spherical(vec3 o) {
    // L1 to L2 arclength
    float r = abs(o.x) + abs(o.y);
    float h = 1.0 - o.x / r;
    // phi = half the L1 arclength * pi/2
    float phi = (r == 0.0)?0.0:((o.y >= 0.0)?h: -h) * radians(90.0);
    // L1 to L2 radius
#ifdef FIX_EQUAL_AREA
    // inverse of https://en.wikipedia.org/wiki/Collignon_projection
    float z = 1.0 - abs(o.z);    
    float theta = radians(90.0) - asin(1.0 - z*z)*sign(o.z);
#else
    float theta = (1.0 - o.z) * radians(90.0);
#endif
    return vec2(phi, theta);
}

// uv is [-pi..pi]x[0..pi]
vec3 spherical_surface(vec2 uv) {
    float phi_s = sin(uv.x);
    float phi_c = cos(uv.x);
    float theta_s = sin(uv.y);
    float theta_c = cos(uv.y);
    return vec3(
        theta_s * phi_c,
        theta_s * phi_s,
        theta_c
    );
}

// [0..1]x[0..1]+offset -> [-1..1]x[-1..1]
// https://en.wikipedia.org/wiki/Peirce_quincuncial_projection
vec2 tile_quincuncial(vec2 p) {
    p = mod(p, 2.0);
    ivec2 q = ivec2(p);
    vec2 uv = p - vec2(q);
    return (uv*2.0-1.0)*((((q.x ^ q.y) & 1) != 0)?-1.0:1.0);
}

// [0..2]x[0..1]+offset -> [-1..1]x[-1..1]
// https://en.wikipedia.org/wiki/Guyou_hemisphere-in-a-square_projection
vec2 tile_guyou(vec2 p) {
    p = vec2(p.x + p.y, p.y - p.x + 1.0)/2.0;
    return tile_quincuncial(p);
}

//// inversion

// n is a normal vector
vec2 spherical_coordinates(vec3 n) {
    return vec2(atan(n.y, n.x), acos(n.z));
}

// point on L1 surface from spherical coordinates
vec3 spherical_to_L1(vec2 uv) {
    // L2 to L1 radius
#ifdef FIX_EQUAL_AREA
    // forward formula of https://en.wikipedia.org/wiki/Collignon_projection
    float theta = radians(90.0) - uv.y;
    float z = (1.0 - sqrt(1.0 - sin(abs(theta))))*sign(theta);
#else
    float z = 1.0 - uv.y / radians(90.0);
#endif
    // L2 to L1 arclength
    float a = uv.x / radians(45.0);
    float r = 1.0 - abs(z);
    a = mod(a + 4.0, 8.0) - 4.0;
    float cos_x = 1.0 - abs(a) / 2.0;
    vec2 xy = r * vec2(cos_x, sign(a)*(1.0 - abs(cos_x)));
    return vec3(xy, z);
}

vec3 L1normalize(vec3 n) {
    return n / (abs(n.x) + abs(n.y) + abs(n.z));
}

// n is a point on the L1 surface
vec2 octahedral_coordinates(vec3 n) {
    //n = L1normalize(n); // not necessary if point is already normalized
    return (n.z >= 0.0)?n.xy:oct_wrap(n.xy);
}

// [-1..1]x[-1..1] -> [0..2]x[0..1]
vec2 inverse_guyou(vec2 o) {    
    o = vec2(o.x - o.y + 1.0/2.0, o.x + o.y - 1.0/2.0);
    o += vec2(-0.5, 0.5);
    if (o.y >= 1.0) {
        o = 2.0 - o;        
    } else if (o.x < -1.0) {
        o.x = o.x + 4.0;
    } else if (o.y < -1.0) {
        o = vec2(2.0,-2.0) - o;
    }
    return (o+1.0)/2.0;
}

// visualization
//////////////////////////////////////////////////////////

float triangle_area(vec3 A, vec3 B, vec3 C) {
    return length(cross(B - A, C - A)) / 2.0;
}

vec2 nanglebasis (vec3 a, vec3 b) {
    float ab = dot(a, b);
    return vec2(ab, sqrt(1.0 - ab * ab));
}

float simplex_solid_angle (vec2 u, vec2 v, vec2 w) {
    float A =
            acos((u.x - v.x * w.x) / (v.y * w.y))
            + acos((v.x - w.x * u.x) / (w.y * u.y))
            + acos((w.x - u.x * v.x) / (u.y * v.y))
            - radians(180.0);
    return (A != A)?0.0:A;
}

float pyramid_points_solid_angle (vec3 A, vec3 B, vec3 C, vec3 D) {
#ifdef USE_TRIANGLE_AREA
    return triangle_area(A, B, C) + triangle_area(C, D, A);
#else
    vec2 ab = nanglebasis(A, B);
    vec2 ac = nanglebasis(A, C);
    vec2 da = nanglebasis(A, D);
    vec2 bc = nanglebasis(B, C);
    vec2 cd = nanglebasis(C, D);
    return simplex_solid_angle(bc, ac, ab) + simplex_solid_angle(da, ac, cd);
#endif
}

vec3 plasma(float t) {

    const vec3 c0 = vec3(0.05873234392399702, 0.02333670892565664, 0.5433401826748754);
    const vec3 c1 = vec3(2.176514634195958, 0.2383834171260182, 0.7539604599784036);
    const vec3 c2 = vec3(-2.689460476458034, -7.455851135738909, 3.110799939717086);
    const vec3 c3 = vec3(6.130348345893603, 42.3461881477227, -28.51885465332158);
    const vec3 c4 = vec3(-11.10743619062271, -82.66631109428045, 60.13984767418263);
    const vec3 c5 = vec3(10.02306557647065, 71.41361770095349, -54.07218655560067);
    const vec3 c6 = vec3(-3.658713842777788, -22.93153465461149, 18.19190778539828);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

vec2 rotate(vec2 p, float a) {
    float c = cos(a); float s = sin(a);
    return vec2(p.x*c + p.y*s, s*p.x - c*p.y);
}

vec3 stereographic_sphere(vec2 uv) {
    vec3 n = vec3(uv, sqrt(1.0 - dot(uv,uv)));
    float t = iTime*0.1;
    n.yz = rotate(n.yz, radians(sin(t*0.25*radians(360.0))*45.0));    
    n.xz = rotate(n.xz, t*radians(360.0));    
    return n;
}

//////////////////////////////////////////////////////////

// for inverse mapping
vec3 transfer_color(vec2 q) {
#ifdef SHOW_GUYOU_MAP
    q = inverse_guyou(q);
    // mirror U coordinate (0-1-0)
    q.x = 1.0-abs(1.0-q.x);
#else
    q = q*0.5 + 0.5;
#endif
#ifdef SHOW_CUBEMAP
    return textureLod(iChannel1, q*4.0, 2.0).rgb;
#else
    q = q - mod(q, 1.0/subdivisions);
    return vec3(q, 0.0);
#endif
}

vec3 transfer_color(vec3 p) {
#ifdef SHOW_CUBEMAP
    p.xy = rotate(p.xy, radians(90.0 + 45.0));
    p.xz = rotate(p.xz, radians(90.0));
    return textureLod(iChannel0, p, 0.0).xyz;
#elif defined(SHOW_INVERSE)
    vec2 q = octahedral_coordinates(spherical_to_L1(spherical_coordinates(p)));
    q = q - mod(q, 1.0/subdivisions);
    return vec3(q*0.5 + 0.5, 0.0);
#else
    return p*0.5 + 0.5;
#endif
}

float flow_lines(vec3 q) {
    float a = atan(q.y, q.x);
#ifdef SHOW_COS_THETA
    float r = q.z*radians(90.0);
#else
    float r = acos(q.z);
#endif
    float A = subdivisions;
    return sin((a-iTime*0.1)*2.0*A)*sin(r*A);
}

float octahedral_map_flow_lines(vec2 p) {
    return flow_lines(normalize(octahedral_surface(p)));    
}

float octahedral_arc_map_flow_lines(vec2 p) {
    return flow_lines(spherical_surface(L1_to_spherical(octahedral_surface(p))));
}

float tiled_octahedral_arc_map_flow_lines(vec2 p) {
    return flow_lines(spherical_surface(L1_to_spherical(octahedral_surface(tile_guyou((p-1.0)/2.0)))));
}

bool in_range(vec2 p) {
    return max(abs(p.x),abs(p.y)) < 1.0;
}

vec3 solid_angle_color(vec3 p00, vec3 p10, vec3 p01, vec3 p11) {
    // ideal solid angle: pi / subdivisions²
    float isa = radians(180.0) / (subdivisions * subdivisions);
    float sa1 = pyramid_points_solid_angle(p00,p10,p11,p01);
    float sa2 = pyramid_points_solid_angle(p10,p00,p01,p11);
    float sa = (uv.x*uv.y < 0.0)?sa1:sa2;
#if 1
    sa = abs(sa/isa-1.0);
#else
    sa = abs(log2(sa/isa));
#endif
    return plasma(clamp(sa,0.0,1.0));
}

void paint_octahedral_map() {
    set_line_width_px(1.2);
    vec2 uv = get_origin();    
    vec3 p = normalize(octahedral_surface(uv));
    set_source_rgb(transfer_color(p));
#ifdef SHOW_SOLID_ANGLE
    float s = 1.0/subdivisions;
    uv = uv - mod(uv, s) + s * 0.5;
    vec2 w = 0.5*vec2(-s,s);
    vec3 p00 = normalize(octahedral_surface(uv + w.xx));
    vec3 p10 = normalize(octahedral_surface(uv + w.yx));
    vec3 p01 = normalize(octahedral_surface(uv + w.xy));
    vec3 p11 = normalize(octahedral_surface(uv + w.yy));
    set_source_rgb(solid_angle_color(p00,p10,p01,p11));
#endif        
    rectangle(-1.0, -1.0, 2.0, 2.0);    
    fill_preserve();    
    set_source_rgb(vec3(0.0));
    stroke();
#ifdef SHOW_FLOW_LINES    
    if (in_range(get_origin())) {
        graph2D(octahedral_map_flow_lines);
        stroke();
    }
#endif
}

void paint_inverse_octahedral_map() {
    set_line_width_px(1.2);
    vec3 n = stereographic_sphere(get_origin());     
    vec2 uv = octahedral_coordinates(L1normalize(n));    
    set_source_rgb(transfer_color(uv));
    circle(0.0, 0.0, 1.0);
    fill_preserve();    
    set_source_rgb(vec3(0.0));
    stroke();
}

void tile_octahedral_arc_map() {
    set_line_width_px(1.5);
#if 1
    vec3 p = spherical_surface(L1_to_spherical(octahedral_surface(tile_guyou((get_origin()+1.0)/2.0))));
    set_source_rgb(transfer_color(p));
#else
    vec2 uv = (get_origin()+1.0)/2.0;
    #if 1
    uv = fract(uv);
    uv = uv*2.0-1.0;
    #endif
    #if 1
    //uv = tile_guyou(uv);
    uv = inverse_guyou(uv);
    #else
    uv = tile_quincuncial(uv);
    #endif
    uv = uv*0.5+0.5;
    if (min(uv.x,uv.y) < 0.0)
        return;
    if (uv.x > 2.0)
        return;   
    if (uv.y > 1.0)
        return; 
    float b = clamp(uv.x - 1.0, 0.0, 1.0);
    uv.x = fract(uv.x);
    set_source_rgb(vec3(uv,b));
#endif
    clear();
    rectangle(-1.0, -1.0, 4.0, 2.0);
    set_source_rgb(vec3(0.0));
    stroke();
#ifdef SHOW_FLOW_LINES
    set_source_rgb(vec3(0.0));
    graph2D(tiled_octahedral_arc_map_flow_lines);
    stroke();
#endif
}

void paint_octahedral_arc_map() {
    set_line_width_px(1.2);

    vec2 uv = get_origin();    
    vec3 p = spherical_surface(L1_to_spherical(octahedral_surface(uv)));
    set_source_rgb(transfer_color(p));
#ifdef SHOW_SOLID_ANGLE
    float s = 1.0/subdivisions;
    uv = uv - mod(uv, s) + s * 0.5;
    vec2 w = 0.5*vec2(-s,s);
    vec3 p00 = spherical_surface(L1_to_spherical(octahedral_surface(uv + w.xx)));
    vec3 p10 = spherical_surface(L1_to_spherical(octahedral_surface(uv + w.yx)));
    vec3 p01 = spherical_surface(L1_to_spherical(octahedral_surface(uv + w.xy)));
    vec3 p11 = spherical_surface(L1_to_spherical(octahedral_surface(uv + w.yy)));
    set_source_rgb(solid_angle_color(p00,p10,p01,p11));
#endif    
    rectangle(-1.0, -1.0, 2.0, 2.0);
    fill_preserve();    
    set_source_rgb(vec3(0.0));
    stroke();
#ifdef SHOW_FLOW_LINES
    if (in_range(get_origin())) {
        graph2D(octahedral_arc_map_flow_lines);
        stroke();
    }
#endif
}

void paint_inverse_octahedral_arc_map() {
    set_line_width_px(1.2);
    vec3 n = stereographic_sphere(get_origin());     
    vec2 uv = octahedral_coordinates(spherical_to_L1(spherical_coordinates(n)));
    set_source_rgb(transfer_color(uv));
    circle(0.0, 0.0, 1.0);
    fill_preserve();
    set_source_rgb(vec3(0.0));
    stroke();
}

void paint() {
    set_source_rgb(vec3(0.2));
    clear();

#ifdef SHOW_TILING
    scale(0.5);
    tile_octahedral_arc_map();
#elif defined(SHOW_INVERSE)
    scale(0.75);
    if (get_origin().x < 0.0) {
        translate(-1.1,0.0);
        paint_inverse_octahedral_map();
    } else {
        translate(1.1,0.0);
        paint_inverse_octahedral_arc_map();
    }
#else
    scale(0.75);
    if (get_origin().x < 0.0) {
        translate(-1.1,0.0);
        paint_octahedral_map();
    } else {
        translate(1.1,0.0);
        paint_octahedral_arc_map();
    }
#endif    
}

//////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init(fragCoord, iMouse.xy, iResolution.xy);

    paint();

    blit(fragColor);
}

#ifdef GLSLSANDBOX
void main() {
    mainImage(gl_FragColor, gl_FragCoord.xy);
}
#endif

