// Render Preview
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the Image Export Buffer
    vec3 color = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    // HDR Tonemapping
    color = tonemap(color);

    // Output the Image Preview
    fragColor = vec4(color, 1.0);
