// ##### Parameters #####

// Camera Field of View
#define CAMERA_FOV 1.0

// Tonemap Exposure
#define EXPOSURE 1.0

// Enable sRGB gamma thingy
#define sRGB_transfer

// Enable Monte-Carlo Bloom
#define BLOOM

// Radius of the Black Hole
#define blackhole_radius 0.6

// Timestep (smaller = slower)
#define delta_t 0.05

// Maximum number of times a light path can bounce before getting yeeted
#define MAX_BOUNCES 4u

// Use Gradient Noise instead of Value Noise (slower, should look nicer)
//#define USE_GRADIENT_NOISE

//
#define MAX_DIST 4.0

// Maximum steps a light path can take before getting yeeted
#define MAX_STEPS 16384u

// Camera Sensor Size (for DoF, unused by default, uncomment it in Buffer A)
