// ##### Constants #####

// http://www.mimirgames.com/articles/programming/digits-of-pi-needed-for-floating-point-numbers/
const float pi     = 3.141592653589793; // Pi
const float two_pi = 6.283185307179586; // 2 * Pi
const float inv_pi = 0.318309886183790; // 1 / Pi

// ##### Maths #####

// Rotate a 2-Component Vector
vec2 rotate(vec2 vector, float theta)
{
    float s = sin(theta), c = cos(theta);
    return vec2(vector.x * c - vector.y * s, vector.x * s + vector.y * c);
}

// Dot Product
// for optimizations involving comparing length, for example:
// min(length(vector0), length(vector1))
// This can be simplified and save using an extra sqrt() by changing it to:
// sqrt(min(dotp(vector0), dotp(vector1)))

float dot_p(vec2 vector) { return dot(vector, vector); }
float dot_p(vec3 vector) { return dot(vector, vector); }
float dot_p(vec4 vector) { return dot(vector, vector); }

// ##### Color Management #####

// Sh*tty HDR Tonemap(tm)
vec3 tonemap(vec3 color)
{
    color = smoothstep( 0.0, 1.0, 1.0 - exp(-color * EXPOSURE) );

    #ifdef sRGB_transfer
    // sRGB Color Component Transfer: https://www.color.org/chardata/rgb/sRGB.pdf
    color  = vec3(
    color.r > 0.0031308 ? (pow(color.r, 1.0 / 2.4) * 1.055) - 0.055 : color.r * 12.92,
    color.g > 0.0031308 ? (pow(color.g, 1.0 / 2.4) * 1.055) - 0.055 : color.g * 12.92,
    color.b > 0.0031308 ? (pow(color.b, 1.0 / 2.4) * 1.055) - 0.055 : color.b * 12.92);
    #endif

    return clamp(color, 0.0, 1.0);
}

