// Every XYZ to sRGB matrix I find is slightly different, so here is my own:
const mat3 XYZ2sRGB = mat3(
 3.240, -1.537, -0.499,
-0.969,  1.876,  0.042,
 0.056, -0.204,  1.057);

// Convert XYZ to sRGB
vec3 xyz2rgb(vec3 color_xyz)
{
    // I can't remember the order of GLSL matrix multiplication,
    // so this function exists instead of doing it every time
    return color_xyz * XYZ2sRGB;
}

// Convert sRGB to XYZ
vec3 rgb2xyz(vec3 color_rgb)
{
    return color_rgb * transpose(XYZ2sRGB);
}

// idk what to cite, here are some shaders that all use this
// https://www.shadertoy.com/view/tsKczy
// https://www.shadertoy.com/view/MslSDl
// https://www.shadertoy.com/view/MttyzB
vec3 blackbodyXYZ(float t)
{
    // https://en.wikipedia.org/wiki/Planckian_locus
    float u = (0.860117757 + 1.54118254E-4 * t + 1.28641212E-7 * t * t) / (1.0 + 8.42420235E-4 * t + 7.08145163E-7 * t * t);
    float v = (0.317398726 + 4.22806245E-5 * t + 4.20481691E-8 * t * t) / (1.0 - 2.89741816E-5 * t + 1.61456053E-7 * t * t);

    // https://en.wikipedia.org/wiki/CIE_1960_color_space
    // https://en.wikipedia.org/wiki/XYZ_color_space

    // Hippitty Hoppity through magic convert the planckian locus to some weird Color space linked to XYZ
    vec2 xyy = vec2(3.0 * u, 2.0 * v) / (2.0 * u - 8.0 * v + 4.0);

    // And then convert from the shart color space to XYZ
    return vec3(xyy.x / xyy.y, 1.0, (1.0 - xyy.x - xyy.y) / xyy.y);
}

// ##### Random Number Generator #####

// Triple32 Hash: https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17u;
    x *= 0xED5AD4BBu;
    x ^= x >> 11u;
    x *= 0xAC4C1B51u;
    x ^= x >> 15u;
    x *= 0x31848BABu;
    x ^= x >> 14u;
    return x;
}

// Random Number Generator Seed
uint ns;

// Uniform Random Value Between 0.0 and 1.0
float rand()
{
    // Update RNG
    ns = triple32(ns);

    return float(ns) / float(0xFFFFFFFFu);
}

// 2-Component Uniform Random Vector
vec2 rand2()
{
    vec2 vector;
    vector.x = rand();
    vector.y = rand();
    return vector;
}

// 3-Component Uniform Random Vector
vec3 rand3()
{
    vec3 vector;
    vector.x = rand();
    vector.y = rand();
    vector.z = rand();
    return vector;
}

// 4-Component Uniform Random Vector
vec4 rand4()
{
    vec4 vector;
    vector.x = rand();
    vector.y = rand();
    vector.z = rand();
    vector.w = rand();
    return vector;
}

// Uniformly distributied random point on a unit circle
vec2 udir2()
{
    float z = rand();
    float r = two_pi * z;
    float s = sin(r), c = cos(r);
    return vec2(s, c);
}

// Uniformly distributed random point on the surface of a unit sphere
vec3 udir3()
{
    vec2 z = rand2();
    vec2 r = vec2( two_pi * z.x, acos(2.0 * z.y - 1.0) );
    vec2 s = sin(r), c = cos(r);
    return vec3(c.x * s.y, s.x * s.y, c.y);
}

// See michael0884's usage of PCG Random
// https://www.shadertoy.com/view/wltcRS
// https://www.shadertoy.com/view/WttyWX

// 2D Gaussian Distribution
vec2 nrand2(vec2 mean, float sigma)
{
    vec2 z = rand2();
    return mean + sigma * sqrt( -2.0 * log(z.x   ) ) * vec2(cos(two_pi * z.y), sin(two_pi * z.y) );
}

// 3D Gaussian Distribution
vec3 nrand3(vec3 mean, float sigma)
{
    vec4 z = rand4();
    return mean + sigma * sqrt( -2.0 * log(z.xxy ) ) * vec3(cos(two_pi * z.z), sin(two_pi * z.z), cos(two_pi * z.w) );
}

// 4D Gaussian Distribution
vec4 nrand4(vec4 mean, float sigma)
{
    vec4 z = rand4();
    return mean + sigma * sqrt( -2.0 * log(z.xxyy) ) * vec4(cos(two_pi * z.z), sin(two_pi * z.z), cos(two_pi * z.w), sin(two_pi * z.w) );
}

// ##### Misc #####

// Blackman-Harris Pixel Filter
vec2 pixel_filter(vec2 pixel_coord)
{
    // Gaussian Pixel Filter
    //return nrand2(pixel_coord, 0.5);

    // https://www.desmos.com/calculator/fcdor4uxyt
    float r = pow(2.0 * acos(1.0 - rand()) / pi, 0.78539821704);
    return pixel_coord + rand() * udir2();

    // https://en.wikipedia.org/wiki/Window_function#Blackmanâ€“Harris_window
    // w[n] = a0-a1*cos(2*pi*n/N)+a2*cos(4*pi*n/N)-a3*cos(6*pi*n/N)
    // a0 = 0.35875; a1 = 0.48829; a2 = 0.14128; a3 = 0.01168
    float n = 0.5 * rand() + 0.5;
    float w = 0.35875 - 0.48829 * cos(2.0 * pi * n) + 0.14128 * cos(4.0 * pi * n) - 0.01168 * cos(6.0 * pi * n);
    return pixel_coord + (udir2() * 2.0 * w);
}

// ##### Rendering #####

// https://www.shadertoy.com/view/4djSRW
vec4 hash44(vec4 p4)
{
    p4  = fract( p4 * vec4(0.1031, 0.1030, 0.0973, 0.1099) );
    p4 += dot(p4, p4.wzxy + 33.33);
    return fract( (p4.xxyz + p4.yzzw) * p4.zywx);
}

float noise(vec3 p, int octave)
{
    #ifdef USE_GRADIENT_NOISE
    // Inigo Quilez Gradient Noise
    // https://www.shadertoy.com/view/Xsl3Dl

    vec3 f = fract(p);
    vec3 i = floor(p);

    vec3 s = smoothstep(0.0, 1.0, f);

    vec3 h0 = hash44( vec4(i + vec3(0, 0, 0), octave) ).xyz;
    vec3 h1 = hash44( vec4(i + vec3(1, 0, 0), octave) ).xyz;
    vec3 h2 = hash44( vec4(i + vec3(0, 1, 0), octave) ).xyz;
    vec3 h3 = hash44( vec4(i + vec3(1, 1, 0), octave) ).xyz;
    vec3 h4 = hash44( vec4(i + vec3(0, 0, 1), octave) ).xyz;
    vec3 h5 = hash44( vec4(i + vec3(1, 0, 1), octave) ).xyz;
    vec3 h6 = hash44( vec4(i + vec3(0, 1, 1), octave) ).xyz;
    vec3 h7 = hash44( vec4(i + vec3(1, 1, 1), octave) ).xyz;

    float v0 = dot( h0, f - vec3(0, 0, 0) );
    float v1 = dot( h1, f - vec3(1, 0, 0) );
    float v2 = dot( h2, f - vec3(0, 1, 0) );
    float v3 = dot( h3, f - vec3(1, 1, 0) );
    float v4 = dot( h4, f - vec3(0, 0, 1) );
    float v5 = dot( h5, f - vec3(1, 0, 1) );
    float v6 = dot( h6, f - vec3(0, 1, 1) );
    float v7 = dot( h7, f - vec3(1, 1, 1) );

    return smoothstep(-0.5, 0.5, mix(
    mix(mix(v0, v1, s.x), mix(v2, v3, s.x), s.y),
    mix(mix(v4, v5, s.x), mix(v6, v7, s.x), f.y),
    s.z)
    );
    #else

    vec3 f = fract(p);
    vec3 i = floor(p);

    vec3 s = smoothstep(0.0, 1.0, f);

    float t0 = hash44( vec4(i + vec3(0, 0, 0), octave) ).x;
    float t1 = hash44( vec4(i + vec3(1, 0, 0), octave) ).x;
    float t2 = hash44( vec4(i + vec3(0, 1, 0), octave) ).x;
    float t3 = hash44( vec4(i + vec3(1, 1, 0), octave) ).x;
    float t4 = hash44( vec4(i + vec3(0, 0, 1), octave) ).x;
    float t5 = hash44( vec4(i + vec3(1, 0, 1), octave) ).x;
    float t6 = hash44( vec4(i + vec3(0, 1, 1), octave) ).x;
    float t7 = hash44( vec4(i + vec3(1, 1, 1), octave) ).x;

    return mix(
    mix(mix(t0, t1, s.x), mix(t2, t3, s.x), s.y),
    mix(mix(t4, t5, s.x), mix(t6, t7, s.x), f.y),
    s.z);
    #endif
}

// Fractal Brownian Motion
float fbm(vec3 p, int iter)
{
    float value = 0.0;
    float accum = 0.0;
    float atten = 0.5;
    float scale = 1.0;

    for(int i = 0; i < iter; i++)
    {
        value += atten * noise(scale * p, iter);
        accum += atten;
        atten *= 0.5;
        scale *= 2.5;
    }

    return accum != 0.0 ? value / accum : value;
}

// Basically copied and pasted from michael0884
// https://rantonels.github.io/starless/
vec3 gravitational_field(vec3 p)
{
    /*
    vec3 r = p / blackhole_radius;
    float R = length(r);
    return -4.0 * 1.5 * r / pow(R, 5.0);
    */

    vec3 r = p / blackhole_radius;
    float R = length(r);
    return -6.0 * r / (R * R * R * R * R); // get rid of that nasty pow() calculation
}

