float sample_volume(in vec3 p, out vec3 c, out vec3 e)
{
    c = vec3(0.3, 0.2, 0.1);
    e = vec3(0);

    //if(dot_p(p.xz) > 8.0 || abs(p.y)  > 0.33)
    if(dot_p(p.xz) > 8.0 || p.y * p.y > 0.3 * 0.3) // I think this might be slightly faster, doesn't matter
    {
        return 0.0;
    }

    float n0 = fbm(20.0 * vec3(rotate( p.xz, (8.0 * p.y) + ( 4.0 * length(p.xz) ) ), p.y).xzy, 8);

    float d_falloff = length(vec3(0.12, 7.50, 0.12) * p);
    float e_falloff = length(vec3(0.20, 8.00, 0.20) * p);

    float t = rand();
    e = xyz2rgb( blackbodyXYZ( (4000.0 * t * t) + 2000.0 ) );
    e = clamp(e / max(max(max(e.r, e.g), e.b), 0.01), 0.0, 1.0);

    //e *= 128.0 * max(n0 - e_falloff, 0.0) / (pow(dot_p(0.5 * p), 1.5) + 0.05);
    e *= 128.0 * max(n0 - e_falloff, 0.0) / (dot_p(0.5 * p) + 0.05);

    return 128.0 * max(n0 - d_falloff, 0.0);
}

vec3 sky_radiance(vec3 dir)
{
    return vec3(0);
}

vec3 radiance(vec3 ro, vec3 rd)
{
    vec3 att = vec3(1); // Light Attenuation
    vec3 r   = vec3(0); // Emission

    vec3 p = ro + (rand() * delta_t * rd);
    vec3 v = rd;

    uint bounces = 0u;

    for(uint i = 0u; i < MAX_STEPS; i++)
    {
        if(bounces > MAX_BOUNCES) // light path bounced too many times, discard the sample
        {
            break;
        }

        if(dot_p(p) < blackhole_radius * blackhole_radius) // light fell into the black hole
        {
            return r;
        }

        if(dot_p(p) > 3.6 * 3.6) // light escaped the scene
        {
            r += att * sky_radiance( normalize(v) );
            return r;
        }

        vec3 volume_color   ;
        vec3 volume_emission;

        float d = sample_volume(p, volume_color, volume_emission);

        r += att * volume_emission * delta_t; // add emission

        if(d > 0.0)
        {
            float absorbance = exp(-1.0 * delta_t * d);

            if( absorbance < rand() ) // check if light decides to bounce
            {
                v = length(v) * reflect( normalize(v), udir3() );

                att *= volume_color;

                bounces++;
            }
        }

        vec3 g = gravitational_field(p); // calculate gravitational influence on the light

        v += g * delta_t; // update velocity
        p += v * delta_t; // update position
    }

    //return vec3(0) ; // accumulate vec3(0)
    return vec3(-1); // discards sample
}

