void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Accumulation Buffer
    fragColor = iFrame != 0 ? texelFetch(iChannel0, ivec2(fragCoord), 0) : vec4(0.0);

    // Initialize the Random Number Generator
    ns = uint(iFrame) * uint(iResolution.x * iResolution.y) + uint(fragCoord.x + fragCoord.y * iResolution.x);
    //INIT_RNG();

    // Screen UV Coordinates
    vec2 uv = 2.0 * (pixel_filter(fragCoord) - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    // Bloom
    #ifdef BLOOM
    float r = rand();
    if(r < 0.10)
    {
        uv = nrand2(uv, rand() * 0.015);
    }
    if(r > 0.90)
    {
        uv = nrand2(uv, rand() * 0.200);
    }
    #endif

    mat3 rotmat = mat3(
     0.8, -0.2, -0.0,
     0.2,  0.8, -0.0,
    -0.0, -0.0,  1.0);

    // Normalize the Rotation Matrix
    rotmat[0] = normalize(rotmat[0]);
    rotmat[1] = normalize(rotmat[1]);
    rotmat[2] = normalize(rotmat[2]);

    /*
    mat3 cam2world;
	{
		vec3 d = normalize(Target-Eye);
		vec3 r = normalize(cross(d, Up));
		vec3 u = cross(r, d);
		cam2world = mat3(r, u, d);
	}
    */

    vec3 ro = vec3(0.0, 0.2, 3.5);
    vec3 rd = normalize(vec3(CAMERA_FOV * uv, -1.0) * rotmat);

    // Render the Sample
    vec3 color = radiance(ro, rd);

    // Accumulate the Sample (reject if Inf or NaN)
    fragColor += any( lessThan(color, vec3(0) ) ) || any( isinf(color) ) || any( isnan(color) ) ? vec4(0) : vec4(color, 1.0);
}

// ##### Image Export #####
// Shadertoy can export renders in the OpenEXR format!
// Just press the little image icon buttom on the bottom right-hand side of the editor.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the Progressive Rendering Buffer
    vec4 texel = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // Compute and Output the Final Color
    fragColor = vec4(texel.a != 0.0 ? texel.rgb / texel.a : texel.rgb, 1.0);
}
