float thc(float a, float b) {
    return tanh(a * cos(b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float sc = 250.;   
    vec2 fpos = fract(sc * uv) - 0.5;
    vec2 ipos = floor(sc * uv) + 0.5;

    float a = atan(ipos.y, ipos.x);

    float k = length(ipos) + 4. * thc(4., length(uv) + 10. * a + iTime);
    float r = log(k + 3. * thc(4., length(uv) * 100. + 4. * iTime));
    r *= .5 + .5 * thc(1.5, 4. * r + a - iTime);

    vec2 v = r * vec2(cos(a), sin(a));
    ipos = floor(v) + 0.5;

    float d = length(fpos) + cos(length(ipos) + iTime);

    fragColor = vec4(d);
}

