float shade(vec3 sh, vec3 dV, SD sd, int ch, bool proj) {
    vec3 dZ = vec3(0,0,1) * SH1_Basis;
    float W = max(0.0, dot(sh, dZ));
    float L = max(0.0, dot(sh, dV));

    float d = sd.d/pixel_radius;
    float w = fwidth(d);
    float b = clamp(d*0.5+0.5,0.0,1.0);

    float albedo = sd.albedo[ch];
    if (sd.emissive && (abs(d) <= 1.0)) {
        L = albedo;
    } else {
        L *= albedo;
    }

    if (proj) {
        return L;
    } else {
        return mix(L, W, b);
    }
    //return W + L*(1.0 - b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    setup_mouse_pos();
    set_resolution(iResolution.xy);
    ivec2 p = ivec2(fragCoord);
    fragColor = vec4(0,0,0,1);

    vec2 uv = (fragCoord / iResolution.xy)*2.0 - 1.0;
    uv *= vec2(iResolution.x/iResolution.y, 1.0);

    bool proj = uv.x > 1.5; 
    if (proj) {
        // raymarch 2D surface
        vec2 ro = vec2(-2.0, 0.0);
        vec2 rd = normalize(vec2(0.0, uv.y) - ro);

        ro += vec2(0.0, 1.2);
        rd = rotate(rd, radians(-37.0));

        float t = 0.0;
        vec2 q;
        int S = 0;
        for (int i = 0; i < 32; ++i) {
            S += 1;
            q = ro + rd * t;
            float d = map(q).d;
            if (d <= 1e-5)
                break;
            t = t + abs(d);
        }
        if (t > 10.0) {
            fragColor = vec4(0.0);
            return;
        }

        uv = q;
    }
    vec2 tuv = ((uv * vec2(iResolution.y/iResolution.x,1.0))*0.5 + 0.5);
    vec3 shr = texture(iChannel0, tuv).xyz;
    vec3 shg = texture(iChannel1, tuv).xyz;
    vec3 shb = texture(iChannel2, tuv).xyz;
#if 0
    vec3 dV = vec3(-normal_map(uv, pixel_radius),1) * SH1_Basis;
    dV = sh_irradiance_probe(dV);    
#else
    vec3 dV = lambert(-normal_map(uv, pixel_radius)) * SH1_Basis;
#endif

#ifdef SHOW_GRADIENT
    vec3 dX0 = vec3(-1,0,1) * SH1_Basis;
    vec3 dX1 = vec3(1,0,1) * SH1_Basis;
    vec3 dY0 = vec3(0,-1,1) * SH1_Basis;
    vec3 dY1 = vec3(0,1,1) * SH1_Basis;
    vec3 dZ = vec3(0,0,1) * SH1_Basis;
    float dx0 = max(0.0,dot(dX0, sh));
    float dx1 = max(0.0,dot(dX1, sh));
    float dy0 = max(0.0,dot(dY0, sh));
    float dy1 = max(0.0,dot(dY1, sh));
    float dz = dot(dZ, sh);
    vec3 W =
          vec3(1.0,0.0,0.0) * dx0
        + vec3(0.0,1.0,1.0) * dx1
        + vec3(0.0,1.0,0.0) * dy0
        + vec3(1.0,0.0,1.0) * dy1
        + vec3(1.0,1.0,0.0) * dz
    ;
    vec3(vec2(dx0+dx1,dy0+dy1), dz);
#endif
    SD sd = map(uv);

    vec3 col = vec3(
        shade(shr, dV, sd, 0, proj),
        shade(shg, dV, sd, 1, proj),
        shade(shb, dV, sd, 2, proj));

    fragColor = vec4(linear_srgb(ACESFitted(max(col * 1.0,vec3(0.0)))),1.0);
}

//#define SHOW_GRADIENT
#define BOUNCE

// for more information, please see my writeup on the 3D case
// https://gist.github.com/paniq/9e1043134de8184df33b3cbf29fcaf37

const float pi = 3.14159265359;
const float tau = 6.28318530718;
const float unit_sphere_area = 2.0 * tau;
const float unit_circle_area = 2.0 * pi;
#if 1

// circular harmonics
// see also https://blackpawn.com/texts/ch/default.html
// and https://valdes.cc/articles/ch.html

const float unit_area = unit_circle_area;
const vec3 SH1_Basis = sqrt(vec3(vec2(2.0),1.0) / unit_circle_area);

vec3 ambient () {
    return vec3(0,0,2.0 * pi);
}

vec3 lambert (vec2 n) {
    return vec3(n * 0.5 * pi, 2.0);
}

vec3 half_lambert (vec2 n) {
    return vec3(n * 0.5 * pi, pi);
}

vec3 light (vec2 n, float sa) {
    return vec3(n * 2.0 * sin(sa / 2.0), sa);
}

#define SHSharpness 1.0 // 2.0
vec3 sh_irradiance_probe(vec3 v) {
    const float sh_c0 = (2.0 - SHSharpness) * 1.0;
    const float sh_c1 = SHSharpness * 2.0 / 3.0;
    return vec3(v.xy * sh_c1, v.z * sh_c0);
}
#else // 3d basis, not going to work here

// for a simulation in 3D, see
// https://www.shadertoy.com/view/XdtSRn

const float unit_area = unit_sphere_area;
const vec4 SH1_Basis = sqrt(vec4(vec3(3.0),1.0) / unit_sphere_area);

vec4 ambient () {
    return vec4(0,0,0,2.0 * tau);
}

vec4 light (vec3 n, float sa) {
    float k = (cos (sa / 4.0));
    return vec4(n * (pi * (1.0 - k * k)), tau * (1.0 - k));
}

#define SHSharpness 1.0 // 2.0
vec4 sh_irradiance_probe(vec4 v) {
    const float sh_c0 = (2.0 - SHSharpness) * 1.0;
    const float sh_c1 = SHSharpness * 2.0 / 3.0;
    return vec4(v.xyz * sh_c1, v.w * sh_c0);
}
#endif

//---------------------------------------------------------------------------------

vec2 rotate(vec2 p, float r) {
    float c = cos(r); float s = sin(r);
    return mat2(c,s,-s,c) * p;
}

// shaded distance
struct SD {
    float d;
    vec3 albedo;
    bool emissive;
};

SD sd_min(SD a, SD b) {
    if (a.d <= b.d)
        return a;
    else
        return b;
}

SD sd_max(SD a, SD b) {
    if (a.d >= b.d)
        return a;
    else
        return b;
}

SD sd_neg(SD a) {
    return SD(-a.d, a.albedo, a.emissive);
}

SD shade(float d, vec3 albedo) {
    return SD(d, albedo, false);
}

SD emitter(float d, vec3 light) {
    return SD(d, light, true);
}

float box(vec2 p, vec2 r) {
    p = abs(p) - r;
    return max(p.x, p.y);
}

float sphere(vec2 p, float r) {
    return length(p) - r;
}

SD slitlight(vec2 p) {
    const vec3 color = vec3(0.01, 0.5, 0.95);
    float angle = max(0.0,cos(atan(p.y, p.x)*6.0));
    if (angle < 0.5)
        angle = 0.0;
    SD d2 = emitter(sphere(p, 0.1),color * angle * 20.0);
    return d2;
}

vec2 mouse_pos;
#define setup_mouse_pos() \
    mouse_pos = ((iMouse.xy / iResolution.xy)*2.0 - 1.0) * vec2(iResolution.x/iResolution.y, 1.0);


SD map(vec2 p) {
    const vec3 color1 = vec3(0.8);
    const vec3 color3 = vec3(0.95, 0.5, 0.01);
    const vec3 color4 = vec3(0.01, 1.0, 0.8);

    SD d = shade(sphere(p, 0.5),color1);

    SD d2 = slitlight(p - vec2(-0.8,-0.1));

    SD d3 = shade(box(p - vec2(-0.5,-0.2), vec2(0.52,0.05)),color1);

    SD d4 = shade(box(rotate(p, radians(90.0+30.5)) - vec2(0.0, 0.0), vec2(0.05,0.8)),color3);
    SD d5 = shade(box(p - vec2(0.0, 0.0), vec2(0.05,0.4)),color3);
    SD d7 = shade(box(p - vec2(0.20, 0.1), vec2(0.05,0.2)),color4);

    SD d6 = emitter(box(rotate(p - vec2(0.25, -0.2),radians(30.5)), vec2(0.01,0.04)),20.0*vec3(1.0,0.9,0.8));

    SD d8 = shade(sphere(p - mouse_pos, 0.05),vec3(1.0));

    return sd_min(d8,sd_min(d6,sd_min(d3,sd_min(d2, sd_max(d,sd_neg(sd_min(d7,sd_min(d4,d5))))))));
}

vec2 normal_map(vec2 p, float eps) {
    return normalize(vec2(
        (map(p + vec2(eps, 0.0)).d - map(p - vec2(eps, 0.0)).d),
        (map(p + vec2(0.0, eps)).d - map(p - vec2(0.0, eps)).d)));
}

//---------------------------------------------------------------------------------

vec2 resolution;
float pixel_radius;

vec2 pixel2uv(ivec2 p) {
    vec2 uv = ((vec2(p) + 0.5) / resolution.xy)*2.0-1.0;
    uv.x *= resolution.x/resolution.y;
    return uv;
}

vec3 occluder(ivec2 p, out SD sd) {
    vec2 uv = pixel2uv(p);
    float r = pixel_radius;
    sd = map(uv);
    float d = sd.d / r;
    if (d > 1.0)
        return vec3(0.0);
    vec2 n = normal_map(uv, r);
    // orthodox: solid angle goes from 100% to 50%, then drops
    // to 0% when the point is inside the surface
    //float opacity = step(0.0,d)*(d*0.5+0.5);
    // benevolent: no discontinuity, 50% visibility on the surface
    //float opacity = max(0.0,(d*0.5+0.5));
    // smooth: no discontinuity, sine curve
    float opacity = sin(max(-1.0,d)*pi*0.5)*0.5+0.5;

    return light(-n, opacity * 2.0 * pi) * SH1_Basis;
}

vec3 propagate(sampler2D iChannel0, ivec2 p, vec2 n, float sa, SD sd, int ch) {
    ivec2 R = ivec2(resolution.xy);
    if ((p.x < 0)||(p.y < 0)||(p.x >= R.x)||(p.y >= R.y))
        return vec3(0.0);
    if (sd.d < -pixel_radius)
        return vec3(0.0);
    // get occluder of neighbor
    SD occ_sd;
    vec3 occ = occluder(p, occ_sd);

    vec3 F = texelFetch(iChannel0, p, 0).xyz;    
    vec3 dV = vec3(n,1) * SH1_Basis;
    // light hitting our interior cell wall
    float L = max(0.0, dot(F, dV * sa));
    // how much of their cell wall is occupied?
    float E = max(0.0, dot(occ, dV * sa));
    float O =
#ifdef BOUNCE
        min(E, 1.0);
#else
        0.0;
#endif
    // subtract occluder from light
    vec3 outsh = dV * L * (1.0 - O);
    vec3 dRV = vec3(-n,1) * SH1_Basis;
    float ref = 0.0;
    if (sd.emissive) {
        if (abs(occ_sd.d) <= pixel_radius) {
            ref = sd.albedo[ch] * E;
        }
    } else {
        ref = O * L * sd.albedo[ch];
    }
    // add emission
    outsh += ref * dRV;
    return outsh;
}

float solid_angle(vec2 a, vec2 b) {
    return acos(dot(normalize(a),normalize(b)));
}

void set_resolution(vec2 res) {
    resolution = res;
    //pixel_radius = 0.5 * sqrt(2.0) /resolution.y;
    pixel_radius = 1.0 * sqrt(2.0) /resolution.y;
}

#define USE_LPV_KERNEL(CH) \
    void mainImage( out vec4 fragColor, in vec2 fragCoord ) \
    { \
        setup_mouse_pos(); \
        set_resolution(iResolution.xy); \
        lpv_kernel(fragColor, fragCoord, iChannel0, CH); \
    }

void lpv_kernel( out vec4 fragColor, in vec2 fragCoord, sampler2D channel, int ch )
{
    ivec2 p = ivec2(fragCoord);

    fragColor = vec4(0.0);

    SD sd = map(pixel2uv(p));

#define PROPAGATE(OFS, N, SA) \
     propagate(channel, p + OFS, N, SA, sd, ch)
#if 1
    int d = 1;
    float x = 2.0;
    float sa1 = solid_angle(vec2(x,-1.0),vec2(x,1.0)); // ~53.13째, projecting to the right side of our pixel
    float sa2 = solid_angle(vec2(x-1.0,1.0),vec2(x,1.0)); // ~18.43째, projecting to the top/bottom side of our pixel
    // note that sa2 = (90째 - sa1) / 2, and analog in the 3D case, sa2 = (4*pi/6 - sa1) / 4
    vec2 dn1 = normalize(vec2(x - 0.5, 1.0)); // normal to the center of the top side of our pixel
    vec2 dn0 = normalize(vec2(x - 0.5, -1.0)); // normal to the center of the bottom side of our pixel
    fragColor.xyz +=
         PROPAGATE(ivec2(-d, 0), vec2(1,0), sa1)
        +PROPAGATE(ivec2(-d, 0), dn1, sa2)
        +PROPAGATE(ivec2(-d, 0), dn0, sa2)
        +PROPAGATE(ivec2(d, 0), vec2(-1,0), sa1)
        +PROPAGATE(ivec2(d, 0), -dn0, sa2)
        +PROPAGATE(ivec2(d, 0), -dn1, sa2)
        +PROPAGATE(ivec2(0,-d), vec2(0,1), sa1)
        +PROPAGATE(ivec2(0,-d), dn1.yx, sa2)
        +PROPAGATE(ivec2(0,-d), dn0.yx, sa2)
        +PROPAGATE(ivec2(0, d), vec2(0,-1), sa1)
        +PROPAGATE(ivec2(0, d), -dn1.yx, sa2)
        +PROPAGATE(ivec2(0, d), -dn0.yx, sa2);
#else
    float x = 1.0;
    float sa1 = solid_angle(vec2(x,-1.0),vec2(x,1.0)); // 90째, projecting to the left side of our pixel
    fragColor.xyz +=
         PROPAGATE(ivec2(-1, 0), vec2(1,0), sa1)
        +PROPAGATE(ivec2(1, 0), vec2(-1,0), sa1)
        +PROPAGATE(ivec2(0,-1), vec2(0,1), sa1)
        +PROPAGATE(ivec2(0, 1), vec2(0,-1), sa1);
#endif
#undef PROPAGATE
}

//---------------------------------------------------------------------------------

// ACES fitted
// from https://github.com/TheRealMJP/BakingLab/blob/master/BakingLab/ACES.hlsl

const mat3 ACESInputMat = mat3(
    0.59719, 0.35458, 0.04823,
    0.07600, 0.90834, 0.01566,
    0.02840, 0.13383, 0.83777
);

// ODT_SAT => XYZ => D60_2_D65 => sRGB
const mat3 ACESOutputMat = mat3(
     1.60475, -0.53108, -0.07367,
    -0.10208,  1.10813, -0.00605,
    -0.00327, -0.07276,  1.07602
);

vec3 RRTAndODTFit(vec3 v)
{
    vec3 a = v * (v + 0.0245786) - 0.000090537;
    vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
    return a / b;
}

vec3 ACESFitted(vec3 color)
{
    color = color * ACESInputMat;

    // Apply RRT and ODT
    color = RRTAndODTFit(color);

    color = color * ACESOutputMat;

    // Clamp to [0, 1]
    color = clamp(color, 0.0, 1.0);

    return color;
}

//---------------------------------------------------------------------------------

float linear_srgb(float x) {
    return mix(1.055*pow(x, 1./2.4) - 0.055, 12.92*x, step(x,0.0031308));
}
vec3 linear_srgb(vec3 x) {
    return mix(1.055*pow(x, vec3(1./2.4)) - 0.055, 12.92*x, step(x,vec3(0.0031308)));
}

float srgb_linear(float x) {
    return mix(pow((x + 0.055)/1.055,2.4), x / 12.92, step(x,0.04045));
}
vec3 srgb_linear(vec3 x) {
    return mix(pow((x + 0.055)/1.055,vec3(2.4)), x / 12.92, step(x,vec3(0.04045)));
}
