float j(vec2 u)
{
   vec4 a = u.xyxy*(vec4(1.,1.,2.,2.)*.05);
   vec4 b = u.xyxy*(vec4(4.,4.,8.,8.)*.05);
   return dot(vec4(texture(iChannel0,a.xy).x,
                   texture(iChannel0,a.zw).x,
                   texture(iChannel0,b.xy).x,
                   texture(iChannel0,b.zw).x),vec4(.5,.25,.125,.0625));
}
vec4 my(vec2 u, vec2 pp)
{
   float d1 = length(u)*.5+.1;
   float d2 = d1*d1;
   float d3 = d2*d1;
   vec4 c = vec4(d3,d2,d1,1.);
        c = vec4(dot(vec4(1.),c*vec4(1.,-1.6,-.1,1.)),
                 dot(vec4(1.),c*vec4(.94,-1.4,-.05,.94)),
                 dot(vec4(1.),c*vec4(1.15,-2.08,.66,.79)),
                 0.);
   float b = 1./dot(u,u);
   u = 2.*u*b;
   u += (j(u+sign(u)*pp.y*vec2(.01,.0))-.5)*.3;
   u *= 1.5;
   vec2 f = normalize(floor(u)+.5);
   u = (fract(u)-.5)*3.;
   vec4 e = mix(vec4(.48,.39,.41,1.),vec4(.99,.89,.54,1.),dot(f,u)*.5+.5);
   float a = max(1.-.1/max(1.-dot(u,u),0.)-.1*b,0.);
   return mix(c,e,a);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 1.*(fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;
    fragColor = my(u, vec2(iTime));
}
