#define R iResolution.xy

// dave hoskins hash
vec4 hash44(vec4 p4)
{
    p4 += 1200.;
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

const ivec2 res = ivec2(pow(2.,9.) + 1.);
//#define res ivec2(R.y+1.)

vec4 getSquare(ivec2 lidx, int sz){
    return (
        texelFetch(iChannel0, (lidx + ivec2(sz,-sz))  % ivec2(R)   ,0) + 
        texelFetch(iChannel0, (lidx + ivec2(-sz,sz))  % ivec2(R)   ,0) +
        texelFetch(iChannel0, (lidx + ivec2(-sz,-sz)) % ivec2(R)   ,0) + 
        texelFetch(iChannel0, (lidx + ivec2(sz,sz))   % ivec2(R)    ,0) 
    )/4.
    ;
}

vec4 getDiamond(ivec2 lidx, int sz){
    return (
        texelFetch(iChannel0,(lidx + ivec2(0,sz) ) % ivec2(R),0) + 
        texelFetch(iChannel0,(lidx + ivec2(sz,0) ) % ivec2(R),0) +
        texelFetch(iChannel0,(lidx + ivec2(-sz,0)) % ivec2(R),0) + 
        texelFetch(iChannel0,(lidx + ivec2(0,-sz)) % ivec2(R),0) 
    )/4.
    ;
}

void mainImage( out vec4 C, in vec2 U){

    C = texelFetch(iChannel0,ivec2(U),0) ;


    ivec2 idx = ivec2(U);

    bool currPixelIsTime = ( idx.x == int(R.x - 1.) && idx.y == int(R.y - 1.) ) ;




    int t = floatBitsToInt(texelFetch(iChannel0,ivec2(R.x-1.,R.y-1.),0).x);

    // Init bs    
    {
        if(iFrame == 0){
            C -= C;
            return;
        }
        // increment time counter pixel
        if(idx.x == int(R.x - 1.) &&idx.y == int(R.y - 1.) ){

            C = vec4(intBitsToFloat(t + 1));
            if(iMouse.z > 0.)
                C -= C;
            return;
        }

        if(t == 0 || iMouse.z > 0.){
            C -= C;
            return;
        }   
        //if(idx.x >= res.x || idx.y >= res.y) return;


        // First step
        if(t == 1){
            if( idx.y == 0 || idx.y == res.y - 1){
                if(idx.x == 0 || idx.x == res.x - 1){
                    C =  hash44(vec4(idx,0.,0));
                }
            }
            return;
        }
    }

    int st = t%2;
    int stId = t/2;


    int chunkSz = (res.x-1)/int(pow(2.,float(stId-1)));

    float roughness = exp(-float(stId)*0.1);
    //roughness = 1.;

    int halfChunkSz = chunkSz/2;




    if(chunkSz <= 1)
        return;




    float bias = 1.;
    float biasMin = -1.;
    float biasMax = 2.;


    #define getBias(r) ((r)*(biasMax - biasMin) + biasMin)*bias
    if(st == 0){
        // Square
        ivec2 lidx = idx - halfChunkSz;
        if(
            lidx.x%chunkSz == 0 
            && lidx.y%chunkSz == 0
        ){
            C = getSquare(lidx, chunkSz) + getBias(hash44(vec4(lidx,0.,iTime)))*roughness;
        }

    }else{
        // Diamond
        ivec2 lidx = idx;

        if(
            (lidx.x + (lidx.y + halfChunkSz) % chunkSz ) % chunkSz == 0 
            && (lidx.y) % halfChunkSz == 0
        ){
            C = getDiamond(lidx, halfChunkSz) + getBias(hash44(vec4(lidx,0.,iTime)))*roughness;
        }
    }
}
