void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set the default background to white
    vec3 bg_col = vec3(1.0, 1.0, 1.0);
    // colour of our circle
    vec3 circ_col = vec3(0.4, 0.5, 0.3);

    // the position of the pixel that will be painted is stored in fragCoord
    vec2 p = fragCoord;

    // the center of the circle
    vec2 center = vec2(0.25 * iResolution.x, 0.75 * iResolution.y);

    float radius = 100.0;
    // play around with this to get a feel for how it controls anti-aliasing
    float smooth_cutoff = 0.01;

    // smoothstep(x, y, z) returns:
    //      0 if z < x, 
    //      1 if z > y,
    //      a "smoothened" (Hermite interpolated) value between 0 and 1 if x < z < y
    // this is where the SDF magic happens: length(p - center) is a distance
    float sdf_factor = smoothstep(radius, 
                                  radius * (1.0 + smooth_cutoff), 
                                  length(p - center));
    // we mix colors, with the ratio determined by the sdf factor
    vec3 col = mix(circ_col, bg_col, sdf_factor);

    // set the colour of pixels in the fragment to be col, with alpha 1.0
    fragColor = vec4(col, 1.0);
}
