float PI = 3.1415926;
float THI = 6.2831853;

vec3 HSV2RGB(vec3 color){//Based on https://www.rapidtables.com/convert/color/hsv-to-rgb.html
    color.x = fract(color.x);
    color = clamp(color,0.,1.);

    float C = color.z * color.y;
    float X = C * (1. - abs(mod(color.x*360./60.,2.) - 1.));
    float m = color.z - C;

    vec3 RGB_1;
    switch(int(color.x*6.)){
        case 0:
            RGB_1 = vec3(C,X,0);
            break;
        case 1:
            RGB_1 = vec3(X,C,0);
            break;
        case 2:
            RGB_1 = vec3(0,C,X);
            break;
        case 3:
            RGB_1 = vec3(0,X,C);
            break;
        case 4:
            RGB_1 = vec3(X,0,C);
            break;
        case 5:
            RGB_1 = vec3(C,0,X);
            break;

    }
    return vec3((RGB_1.x+m),(RGB_1.y+m),(RGB_1.z+m));
}
vec3 hsv2rgb(vec3 c){ //one I found bieng used with in this example
	vec4 K=vec4(1.,2./3.,1./3.,3.);
	return c.z*mix(K.xxx,clamp(abs(fract(c.x+K.xyz)*6.-K.w)-K.x,0.,1.),c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord - .5 * iResolution.xy) / iResolution.y) * 3.;

    vec3 col = vec3(0);

    col += hsv2rgb(vec3(atan(uv.y,uv.x)/THI + iTime / 5. + pow(length(uv),1.5),length(uv)*4.,4.-length(uv)*4.));
    fragColor = vec4(col,1.0);
}
