mat2 rot(float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}


//Yonatan clouds/mountains combined field
vec2 field(in vec3 p) {

	float s,e,f,o;
	vec3 n;n.y=1.;
    e=p.y;
	for(s=1.;s<8e2;s*=1.8)p.xz*=rot(s),n.xz*=rot(s),n+=cos(p*s)*.3,e+=(dot(sin(p.xz*s),vec2(.3)/s));
	n=-normalize(n);
    f-=exp(-e*11.-6.)*dot(n.xz,vec2(1.8));
    e-=n.y*n.y*.1;
    e-=n.z*n.z*.1;
    o-=exp(-e*11.-6.);

    return vec2(max(.2,1.+.4*f),1.+o);
}


vec3 raycast( in vec3 ro, vec3 rd )
{
    float t = 2.5;
    float dt = .025;
    vec3 col= vec3(0.);
    for( int i=0; i<128; i++ )
	{                
        vec2 v = field(ro+t*rd);  
        float c=v.x, f=v.y;
        t+=dt*f;
        dt *= 1.015;
        col = .95*col+ .08*vec3(c*c*c, .9*c*c, 1.2*c);	
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;


    // camera

    vec3 ro = vec3(-4);
    ro.y +=1.;
    ro.yz*=rot(-.9);    
    vec3 rd = normalize( vec3(p,2.)-ro);    
    ro.z +=t*.4;

	// raymarch 

    vec3 col = raycast(ro,rd);


	// shade

    col =  .5 *(log(1.+col));
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );

}
