// original by musk (mu6k) License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// rootmoid function by @ENDESGA

#define M iMouse.xy
#define R iResolution.xy

float rmoid( float x ) {
	return sqrt( abs( x ) ) * sign( x );
}

float rmoid_norm( float x ) {// from 0 to 1
	return ( sqrt( abs( ( x - .5 ) * .5 ) ) * sign( x - .5 ) ) + .5;
}

float f( float x ) {
    return rmoid_norm( x );
}

void mainImage( out vec4 O, in vec2 I ) {
	vec2 uv = I.xy;

	uv = ( I.xy - M + R ) / R - 1.;
    if( M.x <= .0) uv -= .5;
	uv.x = uv.x * R.x / R.y;
	uv *= 4.0;
	float pixel = 4. / R.y;

	vec3 c = vec3( .0 );

	for( int i = 0; i < 3; i++ ) {
		float x = uv.x + pixel * .3333 + float( i ) * pixel * .3333;
		float y = uv.y + pixel;

		float s0 = f( x - pixel );
		float s1 = f( x + pixel );

		if( s0 - pixel <= y && y < s1 + pixel || s1 - pixel <= y && y < s0 + pixel ) {
			c[ i ] += 1.0;
		}
	}

	c += max( .0, 1.0 - abs( mod( uv.x + .5, 1.0 ) - .5 ) / pixel * 0.5 ) * .1 / ( .5 + abs( uv.x * 2. ) * .5 );
	c += max( .0, 1.0 - abs( mod( uv.y + .5, 1.0 ) - .5 ) / pixel * 0.5 ) * .1 / ( .5 + abs( uv.y * 2. ) * .5 );

	O = vec4( pow( c, vec3( .6 ) ), 1.0 );
}
