#define TAU 6.2831853071 
#define sqaureSize 0.8

bool inSquare(vec2 uv){
    return max(abs(uv.x), abs(uv.y)) < sqaureSize*0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy-vec2(0.5, 0.5);
    uv.x *= iResolution.x/iResolution.y;

    if(iFrame == 0){
        float v = inSquare(uv) ? 0. : 1.;
        fragColor = inSquare(uv) ? vec4(hash33(vec3(iFrame, (uv+vec2(1., 1.))*100.)), 1.) : vec4(vec3(0.), 1.);
        return;
    }

    float angle = hash12(vec2(iFrame, 0.))*TAU;
    vec2 p1 = (hash22(vec2(iFrame, 1.))-vec2(0.5))*sqaureSize;
    vec2 p2 = p1+vec2(cos(angle),sin(angle));


    float lineSide = ((p2.x - p1.x) * (uv.y - p1.y) - (p2.y - p1.y) * (uv.x - p1.x));

    float angle2 = angle + sign(lineSide)* TAU*0.25;

    if(hash12(vec2(iFrame, 3.)) < 0.5) angle2 += TAU*0.5;

    float movement =  0.0038;//0.002+0.01*  hash12(vec2(iFrame, 5.));

    if(hash12(vec2(iFrame, 6.)) < 0.8) movement = 0.5/iResolution.x;//0.0001;//hash12(vec2(iFrame, 6.))

    vec2 colPos = uv +movement*vec2(cos(angle2), sin(angle2));

    float lineSide2 = ((p2.x - p1.x) * (colPos.y - p1.y) - (p2.y - p1.y) * (colPos.x - p1.x));


    colPos.x *= iResolution.y/iResolution.x;
    colPos += vec2(0.5, 0.5);

    //float j = 0.01;
    //colPos += (vec2(j)*hash23(vec3(iFrame, fragCoord))-vec2(j*0.5))/iResolution.xy;

    vec4 textureCol = texture(iChannel0, colPos);
    vec4 currentCol = texture(iChannel0, uv);

    if(sign(lineSide) != sign(lineSide2)){
        if(textureCol != vec4(0.) || abs(lineSide) < movement*0.8){
            float r = hash12(vec2(iFrame, 4.));
            //random rgb
            vec3 randRgb = hash32(vec2(iFrame, 2.));
            vec3 col = randRgb;//floor(randRgb+vec3(0.5));
            if(r < 0.25) col = vec3(0.);
            //else if(r <.2) col = vec3(0.);

            fragColor = vec4(col, 1.);//vec4(0.2, 0.98, 0.05, 1.);
            return;
        }
    }

    fragColor = smoothstep(0., 1., textureCol);
}

