void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t = iTime;
    float x1 = 1.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/10.0/x1;
    uv += vec2(t/2.0,t/3.0)/4.0/x1;
    for(int c=0;c<3;c++){
        float scale = 4.5;
        float scale1 = 1.5;
        float s1 = scale1*scale;
        for(int i=0;i<9;i++)
        {
            uv += uv.xx;
            uv = fract(uv/s1)*s1;
            uv=-fract(uv/(2.0-abs((uv.x-uv.y)/(12.0)))-(uv/(2.5+(fract(uv.x+uv.y))))/scale)*scale/scale1+s1;
            uv /= scale1+col.yx;
        	uv=uv.yx+col.xy;
            uv.x /= -scale1;
            col = (col.yzx+col)/2.0;
        }
     col[c] = fract(((uv.y)+(uv.x))/(2.0));
	}

    fragColor = vec4(vec3(col),1.0);

}
