vec2 RotarPuntoPivote(vec2 p,vec2 pXrYr, float theta)
{
     vec2 pp=p;
     vec2 pos=pXrYr;
     pp=pp-pos;
     pp.xy = rotatev2(pp.xy, theta);
     pp=pp+pos;   
     return pp;
}    

