float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
float Noisefv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

float dstFar, tCur, bSize, tWav;
int idObj;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec4 sLen;
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q.y -= - bSize + 0.25 * bSize;
  d = PrSphDf (q, 0.35 * bSize);
  DMIN (1);
  sLen = vec4 (vec3 (bSize) - 0.03, 0.03);
  q = abs (p);
  d = min (min (
     PrRoundBoxDf (q - sLen.wyz, sLen.xww, 0.02),
     PrRoundBoxDf (q - sLen.xwz, sLen.wyw, 0.02)),
     PrRoundBoxDf (q - sLen.xyw, sLen.wwz, 0.02));
  DMIN (2);
  q = p;
  q.y = abs (q.y) - bSize;
  d = PrRoundCylDf (q.xzy, 0.4 * bSize, 0.02, 0.01);
  DMIN (2);
  q = p;
  d = - PrBoxDf (q, vec3 (bSize + sLen.w));
  DMIN (3);
  q = p;
  q.y -= bSize - 0.22 * bSize;
  d = PrSphDf (q, 0.22 * bSize);
  DMIN (4);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float WaveHt (vec2 p)
{
  mat2 qRot;
  vec4 t4, v4;
  vec2 t2;
  float wFreq, wAmp, ht;
  qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  wFreq = 1.;
  wAmp = 1.;
  ht = 0.;
  for (int j = VAR_ZERO; j < 4; j ++) {
    p *= qRot;
    t4 = (p.xyxy + tWav * vec2 (1., -1.).xxyy) * wFreq;
    t4 += 2. * vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw)).xxyy - 1.;
    t4 = abs (sin (t4));
    v4 = (1. - t4) * (t4 + sqrt (1. - t4 * t4));
    t2 = 1. - sqrt (v4.xz * v4.yw);
    t2 *= t2;
    t2 *= t2;
    ht += wAmp * dot (t2, t2);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return ht;
}

vec4 WaveNfH (vec2 p)
{
  vec3 v;
  vec2 e;
  e = vec2 (0.01, 0.);
  p *= 16.;
  for (int j = VAR_ZERO; j < 3; j ++) v[j] = WaveHt (p + ((j == 0) ? e.yy : ((j == 1) ? e.xy : e.yx)));
  return vec4 (- normalize (vec3 (0.4 * (v.x - v.yz), e.x)), v.x);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, hn4;
  vec3 vn, col, ltDir;
  vec2 u;
  float dstObj, rAz, rEl;
  bool isRefl;
  tWav = 0.2 * tCur;
  dstObj = ObjRay (ro, rd);
  isRefl = false;
  if (dstObj < dstFar && idObj == 1) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
    isRefl = true;
  }
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    if (idObj == 2) {
      col4 = vec4 (0.7, 0.7, 0.8, 0.1);
      vn = VaryNf (32. * ro, vn, 1.);
    } else if (idObj == 3) {
      rAz = atan (ro.x, ro.z);
      rEl = asin (ro.y / length (ro));
      u = vec2 (rAz + pi, tan (2. * atan (0.5 * rEl))) / (2. * pi);
      hn4 = mix (WaveNfH (u), WaveNfH (u - vec2 (1., 0.)), u.x);
      vn = StdVuMat (rEl, rAz) * normalize (hn4.xyz);
      col4 = vec4 (mix (vec3 (0.4, 1., 0.8), vec3 (0.3, 0.7, 1.), smoothstep (0.3, 1., hn4.w)), 0.2);
    } else if (idObj == 4) {
      col4 = vec4 (1., 1., 0.9, -1.);
    }
    if (col4.a >= 0.) {
      ltDir = normalize (- ro);
      col = col4.rgb * (0.1 + 0.9 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    } else col = col4.rgb * (0.6 - 0.4 * dot (rd, vn));
    if (isRefl) col *= 0.9;
  } else col = vec3 (0.);
  return clamp (col, 0., 1.);  
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv;
  float el, az, asp, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.35 * pi, 0.35 * pi);
  dstFar = 20.;
  bSize = 2.;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -0.95 * bSize);
  zmFac = 1.5;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = vec2 (2. * tan (0.5 * atan (uv.x / (asp * zmFac))) * asp * zmFac, uv.y);
    rd = vuMat * normalize (vec3 (uvv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (Maxv3 (d), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  }
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}
