void circle(inout vec4 fragColor, vec2 uv, vec2 c, float r, vec3 col)
{
    float l = length(uv.xy - c.xy);
    if (l < r) fragColor += vec4(col.rgb, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 red = vec3   (1.0, 0.0, 0.0);
    vec3 green = vec3 (0.0, 1.0, 0.0);
    vec3 blue = vec3  (0.0, 0.0, 1.0);

    vec2 c_red = vec2  (0.5, 0.3);
    vec2 c_green = vec2(0.3, 0.8);
    vec2 c_blue = vec2 (0.8, 0.8);

    float r = 0.4;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    circle(fragColor, uv, c_red,   r, red);
    circle(fragColor, uv, c_green, r, green);
    circle(fragColor, uv, c_blue,  r, blue);
}
