#define V vec3
#define m(p) (length(mod(p,4.)-2.)-2.)


void mainImage(out vec4 O, vec2 I){

    V a = iResolution, b=iMouse.xyz,
    c = 10.*(b/a-.5)*sign(b),
    d = V(a.y/.5,I+I-a.xy),
    p = d-d; p.x=iTime;
    d.xy *= mat2(sin(vec4(0,100,55,0)+c.x)); 
    d /= length(d);

    c = p;
    float e = I.x/a.x*4.,
    L = float(16),
    z = L;

    for( int i = 1; i < 20; i++ ){
        a = sign(d);
        b = fract(-c*z*a);
        b += step(b,-b);
        a *= b/d;
        b = min(a.xxx,min(a.y,a.z));
        c += d*b/z;
        z *= m(ceil(c*z+d*step(a,b))/z-.5/z)*z<.8?2.:1.;
    }

    O.rgb = (mod(ceil(c*z/2.),3.)+.1)/V(9.+dot(p-c,p-c))*(m(c)*z);
    O *= 1.2-.8 * pow(abs(mod(e+e,2.)-1.),3.);
}
