#define FIX_LO_PIC  0  // = 1 to freeze image in shredded part of picture

float PrSphDf (vec3 p, float r);
float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
float Hashfv3 (vec3 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, qHit;
float tCur, dstFar, pWid, hMax, hFrac, hCur, nCyc, tCyc, tFrac;
int idObj;
const int mxStep = 200;
const float pi = 3.1415927;

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, sWid, fx;
  dMin = dstFar;
  q = p;
  q.y -= hFrac - hCur;
  d = PrRoundBoxDf (q, vec3 (pWid, hMax - hCur, 0.), 0.005);
  q.y += hCur;
  DMINQ (1);
  sWid = 0.1;
  q = p;
  d = abs (q.x) - pWid;
  q.y -= - hMax + hFrac - hCur;
  fx = floor (q.x / sWid);
  q.x -= sWid * (fx + 0.5);
  q.xz = Rot2D (q.xz, 0.2 * pi * (Hashff (fx + 17.33 * nCyc) - 0.5) * (q.y - hCur));
  d = max (d, PrRoundBoxDf (q, vec3 (0.5 * sWid - 0.008, hCur, 0.), 0.005));
  q.x = p.x;
  q.y += hCur - hMax;
  DMINQ (1);
  q = p;
  q.y -= hFrac;
  d = SmoothMax (PrRoundBoxDf (q, vec3 (pWid + 0.15, hMax + 0.15, 0.02), 0.02),
     - PrBox2Df (q.xy, vec2 (pWid, hMax)), 0.02);
  DMINQ (2);
  q.z -= 0.01;
  d = PrBoxDf (q, vec3 (pWid, hMax, 0.01));
  DMINQ (3);
  q = p;
  d = 10. - q.z;
  DMINQ (4);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float FracDf (vec3 p)
{  // (KIFS fractal simplified from leon's "Taste of Noise 7")
  vec3 ip, a;
  float s, gSize, f, w;
  p.x += 0.02 * tFrac;
  gSize = 3.;
  ip = floor (p / gSize);
  p -= (ip + 0.5) * gSize;
  a = vec3 (0.1, 0.2, 0.3) + 0.5 * length (p) + 0.1 * p + ip;
  w = 5. + 3. * sin (2. * pi * Hashfv3 (ip) + 0.7 * tFrac);
  s = gSize;
  f = 0.1;
  for (int j = 0; j < 5; j ++) {
    p.xz = abs (p.xz) - w * f;
    p.yz = Rot2D (p.yz, a.x);
    p.xz = Rot2D (p.xz, a.y);
    p.yx = Rot2D (p.yx, a.z);
    s = SmoothMin (s, PrSphDf (p, f), f);
    f *= 0.5;
  }
  return 0.5 * s;
}

vec2 FracRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  int nStep;
  dHit = 0.;
  nStep = 0;
  for (int j = VAR_ZERO; j < mxStep; j ++) {
    d = FracDf (ro + dHit * rd);
    ++ nStep;
    if (d < 0.01 || dHit > dstFar) break;
    dHit += d;
  }
  if (nStep == mxStep) nStep = 0;
  return vec2 (dHit, float (nStep));
}

vec3 FracNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = FracDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 FracCol (vec2 p)
{
  vec3 ro, rd, col, vn;
  vec2 ff;
  float dstFrac;
  ro = vec3 (0., 0., -10.);
  rd = normalize (vec3 (p, 5.));
  ff = FracRay (ro, rd);
  col = vec3 (0.);
  if (ff.x < dstFar) {
    ro += ff.x * rd;
    if (ff.y > 0.) {
      vn = FracNf (ro);
      col = HsvToRgb (vec3 (mod (ff.y / float (mxStep) + 0.03 * tFrac, 1.), 0.9, 1.)) * vn.z * vn.z;
      col = pow (clamp (col, 0., 1.), vec3 (0.7));
    }
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, t;
  pWid = 1.;
  hMax = 1.3;
  hFrac = 0.7 * hMax;
  t = fract (tCur / tCyc);
  nCyc = floor (tCur / tCyc);
  hCur = hMax * (0.1 + 0.85 * (1. - smoothstep (0.75, 0.8, t)) * smoothstep (0.1, 0.4, t)) - 0.15;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col4 = vec4 (1., 1., 1., 0.);
      if (qHit.z < 0.) {
        tFrac = tCur;
#if FIX_LO_PIC
        if (ro.y < hFrac - hMax) tFrac = nCyc * tCyc;
#endif
        qHit.y += 0.05;
        if (PrBox2Df (qHit.xy, vec2 (pWid)) < -0.05) col4 = vec4 (FracCol (qHit.xy / pWid +
           vec2 (1., 1.1)), -1.);
      }
    } else if (idObj == 2) {
      col4 = vec4 (0.5, 0.4, 0.2, 0.2);
    } else if (idObj == 3) {
      col4 = vec4 (0.6, 0.3, 0.1, 0.) * (0.8 + 0.2 * Fbm2 (64. * ro.xy));
    } else if (idObj == 4) {
      col4 = vec4 (0.4, 0., 0., 0.);
      vn = VaryNf (32. * ro, vn, 1.);
    }
    if (col4.a >= 0.) {
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
      col *= 1. - smoothstep (0.2, 0.6, dstObj / dstFar);
    } else col = col4.rgb;
  } else {
    col = vec3 (0.);
  }
  return clamp (col, 0., 1.);
}

#define AA  1   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCyc = 15.;
  t = smoothstep (0.15, 0.25, 0.5 - abs (fract (tCur / tCyc) - 0.5));
  if (abs (uv.x) < 1.1 - 0.4 * t) {
    az = 0.;
    el = 0.;
    if (mPtr.z > 0.) {
      az += 2. * pi * mPtr.x;
      el += pi * mPtr.y;
    }
    az = clamp (az, -0.6 * pi, 0.6 * pi);
    el = clamp (el, -0.3 * pi, 0.3 * pi);
    vuMat = StdVuMat (el, az);
    ro = vuMat * mix (vec3 (0., 0.9, -7.), vec3 (0., 0., -12.), t);
    zmFac = 4.5;
    dstFar = 100.;
    ltDir = normalize (vec3 (0.5, 1., -1.));
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
      col += (1. / naa) * ShowScene (ro, rd);
    }
  } else col = vec3 (0.82);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}
