// Light Spectrum Therapy
// when the spectrum hit at the right spot

// main code is in Buffer A
// Buffer B is a minimal temporal anti aliasing
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

float material;
float glow;

// signed distance function
float map(vec3 p)
{
    float dist = 100.;
    float shape = 100.;
    float shape2 = 100.;
    vec3 pp = p;
    float scale = smoothstep(2., .0, abs(p.y));

    // sphere
    dist = length(p)-1.;

    // eyes
    p = pp;
    p.z -= 1.;
    p.x = abs(p.x)-.2;
    shape = min(shape, sdCappedTorus(p, vec2(sin(1.),cos(1.)), .1, .001));

    // smile
    p = pp + vec3(0,.2,-.95);
    p.y *= -1.;
    p.y += cos(abs(p.x)*20.)*.02;
    shape = min(shape, sdCappedTorus(p, vec2(sin(1.),cos(1.)), .2, .001));

    // segments
    p = pp;
    float time = (iTime*2.);
    float anim = fract(time);
    float index = floor(time);
    p = rndrot(p, hash41(index));
    float c = pModPolar(p.xz, floor(100.*hash11(index)));
    scale = smoothstep(2., .0, abs(p.y));
    p.x -= .5+.5*scale;
    p.y += (fract(time+hash11(c))*2.-1.)*10.*(.5+.5*hash11(index+96.));
    shape2 = sdSegment(p, 2., .01) + .1*(1.-sin(anim*3.14));

    // union
    material = shape2 < shape ? 2. : 1.;
    shape = min(shape, shape2);

    // glow
    glow += .001/shape;

    // union
    material = shape < dist ? material : 0.;
    dist = min(dist, shape);

    return dist;
}

void coloring (inout vec3 color, in vec3 pos, in vec3 normal, in vec3 ray, in vec2 uv, in float shade)
{
    // reflecting metal
    if (material == 0.)
    {
        color = vec3(.0);
        float light = dot(reflect(ray, normal), vec3(0,1,0))*.5+.5;
        color += vec3(0.5)*pow(light, 4.5);

        color *= pow(shade,.5);
    }
    // light
    else 
    {
        color = vec3(1);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;

    // background
    vec3 color = vec3(.2)*smoothstep(2.,.5,length(uv));

    // coordinates
    vec3 pos = vec3(0,.1,3);
    vec3 at = vec3(0);
    pos.xz *= rot(cos(iTime*.1)*.5);
    pos.zy *= rot(sin(iTime*.2)*.2);
    vec3 ray = lookAt(pos, at, uv, 1.);

    // noise
    vec3 blue = texture(iChannel0, fragCoord/1024.).xyz;
    vec3 white = hash33(vec3(fragCoord, iFrame));

    // materialing
    material = 0.;
    glow = 0.;
    float maxDist = 10.;

    // raymarch
    const float count = 50.;
    float steps = 0.;
    float total = 0.;
    for (steps = count; steps > 0.; --steps) {
        float dist = map(pos);
        if (dist < total/iResolution.y || total > maxDist) break;
        dist *= 0.9+0.1*blue.z;
        ray += white * total*.01;
        pos += ray * dist;
        total += dist;
    }

    // NuSan https://www.shadertoy.com/view/3sBGzV
    vec2 noff = vec2(.001,0);
    vec3 normal = normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));

    // coloring
    float shade = steps/count;
    if (shade > .001 && total < maxDist) {
        coloring(color, pos, normal, ray, uv, shade);

        // reflection
        if (material == 0.)
        {
            const float count2 = 30.;
            ray = reflect(ray, normal);
            ray = (ray+ blue*.5);
            steps = 0.;
            total = 0.;
            for (steps = count2; steps > 0.; --steps) {
                float dist = map(pos);
                if (dist < total/iResolution.y || total > maxDist) break;
                pos += ray * dist;
                total += dist;
            }
            shade = steps/count2;
            if (shade > .001 && total < maxDist) {
                normal = normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));
                coloring(color, pos, normal, ray, uv, shade);
            }
         }
    }

    // rainbow glow
    if (material == 2.)
    {
        vec3 tint = .5+.5*cos(vec3(0.,.3,.6)*6.+uv.y*3.+iTime+atan(pos.x,pos.z));
        color += tint * max(0., glow);
    }

    fragColor = vec4(color, 1);
}

#define repeat(p,r) (mod(p,r)-r/2.)
mat2 rot(float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }
vec3 lookAt (vec3 from, vec3 at, vec2 uv, float fov)
{
  vec3 z = normalize(at-from);
  vec3 x = normalize(cross(z, vec3(0,1,0)));
  vec3 y = normalize(cross(x, z));
  return normalize(z * fov + uv.x * x + uv.y * y);
}
// Mercury
// https://mercury.sexy/hg_sdf/
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 6.28/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float sdSegment( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}
float sdLink( vec3 p, float le, float r1, float r2 )
{
  vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
  return length(vec2(length(q.xy)-r1,q.z)) - r2;
}
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

// blackle
// https://suricrasia.online/demoscene/functions/
vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}
vec3 rndrot(vec3 p, vec4 rnd) {
  return erot(p, normalize(tan(rnd.xyz)), rnd.w*3.1415);
}

// Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}
vec4 hash41(float p)
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}

// Temporal Anti Aliasing from:
// https://www.elopezr.com/temporal-aa-and-the-quest-for-the-holy-trail/

// but only the color clamping...

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    vec3 temporal = texture(iChannel1, uv).rgb;
    vec3 minColor = vec3(9999.), maxColor = vec3(-9999.);
    for(int x = -1; x <= 1; ++x){
        for(int y = -1; y <= 1; ++y){
            vec3 c = texture(iChannel0, uv + vec2(x, y) / iResolution.xy).rgb;
            minColor = min(minColor, c);
            maxColor = max(maxColor, c);
        }
    }
    temporal = clamp(temporal, minColor, maxColor);
    fragColor.rgb = mix(color, temporal, 0.9);
    fragColor.a = 1.0;
}
