float time;
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    float repa = TAU/8.;
    float angle = atan(p.z, p.x);
    float b = mod(angle+repa*.5,repa)-repa*.5;
    p.xz = vec2(sin(b), cos(b))*length(p.xz);

    float lower = max(p.z-1.+p.y, -p.y-1.);
    acc = _min(acc, vec2(lower, 0.));


    float higher = max(max(p.z-1.-p.y-2.01, p.y+1.), -p.y-1.5);
    acc = _min(acc, vec2(higher, 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x*.5;
    }
    return vec3(-1.);
}

vec3 getEnv(vec3 v)
{
    return texture(iChannel0, v*vec3(1.,-1.,1.)).xxx;
}

vec3 rdr(vec2 uv, float ior)
{
    uv *= .75;
    vec3 col = vec3(0.);
    float t = time*.2;
    float d = 10.;
    vec3 ro = vec3(sin(t)*d,-2,cos(t)*d);
    vec3 ta = vec3(0.,-.5,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 256);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        vec3 refl = normalize(reflect(rd, n));
        float reflTex = getEnv(refl).x;
        col = (pow(reflTex, 5.)*vec3(0.804,0.443,0.996)*2.
        +reflTex*vec3(0.580,1.000,0.839)*.5)*.2
        +getEnv(refract(rd, n, ior));
        ;
    }
    else
    {
        col = pow(getEnv(rd).x, 2.5)*vec3(.5);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    time = iTime+texture(iChannel1, fragCoord/8.).x*.1;
    uv += texture(iChannel1, fragCoord/8.+iTime).xx*.001;
    vec3 col = vec3(0.);
    col.x = rdr(uv, .1).x;
    col.y = rdr(uv, .2).y;
    col.z = rdr(uv, .3).z;

    col = sat(col);
    col = pow(col, vec3(1.3));
    col = mix(col, texture(iChannel2, fragCoord/iResolution.xy).xyz, .9);
    fragColor = vec4(col,1.0);
}

